### **1. Конфигурация Webpack (`webpack.config.js`)**
```javascript
const path = require('path');
const HtmlWebpackPlugin = require('html-webpack-plugin');
const { CleanWebpackPlugin } = require('clean-webpack-plugin');
const CopyWebpackPlugin = require('copy-webpack-plugin');
const MiniCssExtractPlugin = require('mini-css-extract-plugin');
const TerserPlugin = require('terser-webpack-plugin');
const { GenerateSW } = require('workbox-webpack-plugin'); // Для PWA (опционально)

// Пути
const PUBLIC_DIR = path.resolve(__dirname, 'public');
const SRC_DIR = path.resolve(__dirname, 'src');
const DIST_DIR = path.resolve(__dirname, 'dist');

// Плагины для генерации файлов
const plugins = [
  new CleanWebpackPlugin(), // Очистка папки dist перед сборкой
  new HtmlWebpackPlugin({
    template: path.join(PUBLIC_DIR, 'index.html'),
    filename: 'index.html',
  }),
  new CopyWebpackPlugin({
    patterns: [
      { from: path.join(PUBLIC_DIR, 'images'), to: 'images' }, // Копирование статических изображений
      { from: path.join(PUBLIC_DIR, 'fonts'), to: 'fonts' },   // Копирование шрифтов
    ],
  }),
  new MiniCssExtractPlugin({
    filename: 'styles/[name].[contenthash].css', // Оптимизация CSS
  }),
  new GenerateSW({ // Опционально: Service Worker для PWA
    clientsClaim: true,
    skipWaiting: true,
  }),
];

// Модули для обработки файлов
const modules = {
  rules: [
    // TypeScript (если используется)
    {
      test: /\.tsx?$/,
      use: 'ts-loader',
      exclude: /node_modules/,
    },
    // JavaScript
    {
      test: /\.js$/,
      exclude: /node_modules/,
      use: {
        loader: 'babel-loader',
        options: {
          presets: ['@babel/preset-env'],
        },
      },
    },
    // Шаблоны Pug/Jade
    {
      test: /\.pug$/,
      use: ['pug-loader'],
    },
    // SCSS/SASS
    {
      test: /\.scss$/,
      use: [
        MiniCssExtractPlugin.loader,
        'css-loader',
        'sass-loader',
      ],
    },
    // Изображения (оптимизация)
    {
      test: /\.(png|jpg|gif|svg)$/,
      use: [
        {
          loader: 'file-loader',
          options: {
            name: '[name].[hash].[ext]',
            outputPath: 'images',
          },
        },
      ],
    },
    // Шрифты
    {
      test: /\.(woff|woff2|eot|ttf|otf)$/,
      use: [
        {
          loader: 'file-loader',
          options: {
            name: '[name].[hash].[ext]',
            outputPath: 'fonts',
          },
        },
      ],
    },
  ],
};

// Оптимизация сборки
const optimization = {
  minimize: true,
  minimizer: [
    new TerserPlugin(), // Минимизация JS
  ],
  splitChunks: {
    chunks: 'all',
  },
  runtimeChunk: 'single',
};

module.exports = {
  entry: {
    main: path.join(SRC_DIR, 'index.ts'), // Точка входа (TypeScript)
  },
  output: {
    path: DIST_DIR,
    filename: 'scripts/[name].[contenthash].js',
    publicPath: '/', // Для корректной работы в production
  },
  module: modules,
  plugins: plugins,
  optimization: optimization,
  devServer: {
    static: PUBLIC_DIR,
    hot: true,
    compress: true,
    port: 3000,
  },
};
```

