# Реализация Игры Жизни с использованием CUDA и MAPS-Multi

Ниже представлена реализация Игры Жизни с оптимизированным CUDA-ядром для работы на нескольких GPU с использованием фреймворка MAPS-Multi.

## Основные компоненты реализации

### 1. Заголовочный файл `game_of_life.h`

```cpp
#ifndef GAME_OF_LIFE_H
#define GAME_OF_LIFE_H

#include <cuda_runtime.h>
#include <vector>
#include <string>
#include <fstream>
#include <iostream>
#include <stdexcept>
#include <cassert>

#ifdef MAPS_MULTI
#include <maps_multi/maps_multi.h>
#endif

// Константы для правил Игры Жизни
const int NEIGHBORHOOD_SIZE = 8;
const int BIRTH_THRESHOLD = 3;
const int SURVIVAL_MIN = 2;
const int SURVIVAL_MAX = 3;

// Структура для хранения состояния сетки
struct GridState {
    std::vector<bool> current;
    std::vector<bool> next;
    size_t width;
    size_t height;
};

// Функция инициализации сетки случайными значениями
void initialize_grid(GridState& grid, float density = 0.2f);

// CUDA-ядро для вычисления следующего состояния клетки
__global__ void compute_next_state_kernel(
    const bool* current,
    bool* next,
    size_t width,
    size_t height,
    int neighborhood_size,
    int birth_threshold,
    int survival_min,
    int survival_max);

// Функция для выполнения одного шага симуляции
void step(GridState& grid);

// Функция для сохранения сетки в PPM формат
void save_grid_to_ppm(const GridState& grid, const std::string& filename);

// Функция для выполнения регрессионного теста
bool run_regression_test(
    const GridState& expected,
    const GridState& actual,
    const std::string& test_name,
    float tolerance = 0.0f);

// Основная функция симуляции с поддержкой MAPS-Multi
void simulate_game_of_life(
    GridState& grid,
    int steps,
    bool use_maps_multi = false,
    int device_count = 1);

#endif // GAME_OF_LIFE_H
```

### 2. Реализация CUDA-ядра и основных функций `game_of_life.cpp`

```cpp
#include "game_of_life.h"

// Инициализация сетки случайными значениями
void initialize_grid(GridState& grid, float density) {
    grid.width = 1024; // Примерное значение, можно сделать параметром
    grid.height = 1024;
    grid.current.resize(grid.width * grid.height);
    grid.next.resize(grid.width * grid.height);

    std::srand(std::time(nullptr));
    for (size_t i = 0; i < grid.current.size(); ++i) {
        grid.current[i] = (static_cast<float>(std::rand()) / RAND_MAX) < density;
    }
}

// CUDA-ядро для вычисления следующего состояния клетки
__global__ void compute_next_state_kernel(
    const bool* current,
    bool* next,
    size_t width,
    size_t height,
    int neighborhood_size,
    int birth_threshold,
    int survival_min,
    int survival_max) {

    // Вычисляем индексы текущей клетки
    size_t x = blockIdx.x * blockDim.x + threadIdx.x;
    size_t y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    size_t idx = y * width + x;
    int live_neighbors = 0;

    // Проверяем всех соседей
    for (int dy = -1; dy <= 1; ++dy) {
        for (int dx = -1; dx <= 1; ++dx) {
            if (dx == 0 && dy == 0) continue; // Пропускаем саму клетку

            size_t nx = x + dx;
            size_t ny = y + dy;

            // Проверяем границы
            if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                size_t nidx = ny * width + nx;
                if (current[nidx]) {
                    live_neighbors++;
                }
            }
        }
    }

    // Применяем правила Игры Жизни
    bool is_alive = current[idx];
    bool will_survive = (live_neighbors >= survival_min) && (live_neighbors <= survival_max);
    bool will_birth = !is_alive && (live_neighbors == birth_threshold);

    next[idx] = will_survive || will_birth;
}

// Функция для выполнения одного шага симуляции
void step(GridState& grid) {
    // Запускаем CUDA-ядро
    dim3 block_size(16, 16);
    dim3 grid_size(
        (grid.width + block_size.x - 1) / block_size.x,
        (grid.height + block_size.y - 1) / block_size.y
    );

    compute_next_state_kernel<<<grid_size, block_size>>>(
        grid.current.data(),
        grid.next.data(),
        grid.width,
        grid.height,
        NEIGHBORHOOD_SIZE,
        BIRTH_THRESHOLD,
        SURVIVAL_MIN,
        SURVIVAL_MAX
    );

    cudaDeviceSynchronize();
    cudaError_t err = cudaGetLastError();
    if (err != cudaSuccess) {
        throw std::runtime_error("CUDA kernel launch failed: " + std::string(cudaGetErrorString(err)));
    }

    // Меняем буферы местами
    std::swap(grid.current, grid.next);
}

// Сохранение сетки в PPM формат
void save_grid_to_ppm(const GridState& grid, const std::string& filename) {
    std::ofstream out(filename, std::ios::binary);
    if (!out) {
        throw std::runtime_error("Failed to open file for writing: " + filename);
    }

    out << "P6\n" << grid.width << " " << grid.height << "\n255\n";

    for (size_t y = 0; y < grid.height; ++y) {
        for (size_t x = 0; x < grid.width; ++x) {
            size_t idx = y * grid.width + x;
            unsigned char pixel = grid.current[idx] ? 255 : 0;
            out << pixel << pixel << pixel; // Черно-белый
        }
    }
}

// Регрессионный тест
bool run_regression_test(
    const GridState& expected,
    const GridState& actual,
    const std::string& test_name,
    float tolerance) {

    if (expected.width != actual.width || expected.height != actual.height) {
        std::cerr << "Test " << test_name << " failed: grid dimensions don't match\n";
        return false;
    }

    size_t mismatches = 0;
    for (size_t i = 0; i < expected.current.size(); ++i) {
        if (expected.current[i] != actual.current[i]) {
            mismatches++;
        }
    }

    if (mismatches > 0) {
        std::cerr << "Test " << test_name << " failed: "
                  << mismatches << " mismatches found\n";
        return false;
    }

    std::cout << "Test " << test_name << " passed\n";
    return true;
}

// Основная функция симуляции с поддержкой MAPS-Multi
void simulate_game_of_life(
    GridState& grid,
    int steps,
    bool use_maps_multi,
    int device_count) {

    if (use_maps_multi) {
#ifdef MAPS_MULTI
        // Инициализация MAPS-Multi
        maps_multi::Context context;
        context.initialize(device_count);

        // Копируем данные на все устройства
        std::vector<maps_multi::Buffer> current_buffers(device_count);
        std::vector<maps_multi::Buffer> next_buffers(device_count);

        for (int i = 0; i < device_count; ++i) {
            current_buffers[i] = context.create_buffer(grid.current.size() * sizeof(bool), i);
            next_buffers[i] = context.create_buffer(grid.current.size() * sizeof(bool), i);

            context.copy_to_device(
                current_buffers[i],
                grid.current.data(),
                grid.current.size() * sizeof(bool),
                0
            );
        }

        // Выполняем шаги на всех устройствах
        for (int step = 0; step < steps; ++step) {
            for (int i = 0; i < device_count; ++i) {
                dim3 block_size(16, 16);
                dim3 grid_size(
                    (grid.width + block_size.x - 1) / block_size.x,
                    (grid.height + block_size.y - 1) / block_size.y
                );

                context.launch_kernel(
                    compute_next_state_kernel,
                    grid_size,
                    block_size,
                    current_buffers[i],
                    next_buffers[i],
                    grid.width,
                    grid.height,
                    NEIGHBORHOOD_SIZE,
                    BIRTH_THRESHOLD,
                    SURVIVAL_MIN,
                    SURVIVAL_MAX,
                    i
                );
            }

            // Синхронизация и обмен данными между устройствами
            context.synchronize();

            // Копируем результаты обратно на хост
            for (int i = 0; i < device_count; ++i) {
                context.copy_from_device(
                    grid.next.data(),
                    next_buffers[i],
                    grid.next.size() * sizeof(bool),
                    0
                );
            }

            // Меняем буферы местами
            std::swap(grid.current, grid.next);
        }

        // Освобождаем ресурсы
        for (int i = 0; i < device_count; ++i) {
            context.destroy_buffer(current_buffers[i]);
            context.destroy_buffer(next_buffers[i]);
        }
#else
        throw std::runtime_error("MAPS-Multi not available, but use_maps_multi=true");
#endif
    } else {
        // Обычная симуляция на одном устройстве
        for (int i = 0; i < steps; ++i) {
            step(grid);
        }
    }
}
```

### 3. Пример использования `main.cpp`

```cpp
#include "game_of_life.h"
#include <chrono>

int main() {
    try {
        // Инициализация сетки
        GridState grid;
        initialize_grid(grid, 0.1f);

        // Параметры симуляции
        int steps = 100;
        bool use_maps_multi = true; // Включить MAPS-Multi
        int device_count = 2;      // Количество GPU

        // Запуск симуляции
        auto start = std::chrono::high_resolution_clock::now();
        simulate_game_of_life(grid, steps, use_maps_multi, device_count);
        auto end = std::chrono::high_resolution_clock::now();

        // Сохранение результата
        save_grid_to_ppm(grid, "gol_result.ppm");

        // Вывод времени выполнения
        auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start);
        std::cout << "Simulation completed in " << duration.count() << " ms\n";

