    // 1. Запускаем на GPU вычисление всех парных расстояний
    // 2. Сортируем результаты для каждой точки
    // 3. Выбираем k ближайших соседей

    // Для простоты покажем только общую структуру
    std::cout << "Построение графа на GPU..." << std::endl;

    // В реальной реализации здесь будет CUDA код
    // Например, с использованием CUDA ML или собственной реализации

    // Для тестового случая просто создадим пустой граф
    // В реальном коде это будет заполнено значениями с GPU
    for (int i = 0; i < graph.points.size(); ++i) {
        graph.adjacency[i].resize(graph.k);
        graph.distances[i * graph.k] = 0.0f; // Пример значения
    }
}

// Функция для уточнения графа (например, удаление точек)
void refine_graph(KNNGraph& graph, int subset_size) {
    if (subset_size <= 0 || subset_size > graph.points.size()) {
        std::cerr << "Некорректный размер подмножества" << std::endl;
        return;
    }

    // Создаем новый граф для подмножества
    KNNGraph refined_graph;
    refined_graph.is_gpu = graph.is_gpu;
    refined_graph.debug_mode = graph.debug_mode;

    // Копируем только нужные точки
    for (int i = 0; i < subset_size; ++i) {
        refined_graph.points.push_back(graph.points[i]);
    }

    // Реконструируем граф для подмножества
    if (graph.is_gpu) {
        build_knn_graph_gpu(refined_graph);
    } else {
        build_knn_graph_cpu(refined_graph);
    }

    // Обновляем ссылки в оригинальном графе
    graph.points.resize(subset_size);
    graph.adjacency.resize(subset_size);
    graph.distances.resize(subset_size * graph.k);

    // Копируем данные
    for (int i = 0; i < subset_size; ++i) {
        graph.points[i] = refined_graph.points[i];
        graph.adjacency[i] = refined_graph.adjacency[i];
        std::copy(refined_graph.distances.begin() + i * graph.k,
                 refined_graph.distances.begin() + (i+1) * graph.k,
                 graph.distances.begin() + i * graph.k);
    }
}

// Функция для выполнения запроса на графе KNN
std::vector<int> query_knn(const KNNGraph& graph, const Point& query_point) {
    std::vector<int> result;

    if (graph.is_gpu) {
        // В реальной реализации будет GPU версия запроса
        std::cout << "Запрос на GPU..." << std::endl;
        // Здесь нужно реализовать параллельное поиск ближайших соседей
    } else {
        // CPU версия запроса
        std::vector<std::pair<float, int>> distances;

        for (int i = 0; i < graph.points.size(); ++i) {
            float distance = 0.0f;
            for (int d = 0; d < graph.dim; ++d) {
                distance += pow(query_point.data[d] - graph.points[i].data[d], 2);
            }
            distance = sqrt(distance);

            distances.emplace_back(distance, i);
        }

        // Сортируем по расстоянию
        std::sort(distances.begin(), distances.end());

        // Добавляем первых k соседей
        for (int l = 0; l < graph.k && l < distances.size(); ++l) {
            result.push_back(distances[l].second);
        }
    }

    return result;
}

// Функция для генерации эталонных данных
void generate_ground_truth(const KNNGraph& graph, std::vector<std::vector<int>>& ground_truth) {
    ground_truth.resize(graph.points.size());

    for (size_t i = 0; i < graph.points.size(); ++i) {
        // Для простоты возьмем ближайших соседей из графа
        // В реальном случае это могут быть другие метки
        ground_truth[i] = graph.adjacency[i];
    }
}

// Функция для оценки графа KNN
float evaluate_knn(const KNNGraph& graph, const std::vector<std::vector<int>>& ground_truth) {
    float accuracy = 0.0f;
    int total = 0;

    for (size_t i = 0; i < graph.points.size(); ++i) {
        // Сравниваем запросы с эталонными данными
        // Для простоты считаем, что ближайший сосед в запросе должен совпадать с первым в эталоне
        if (!graph.adjacency[i].empty() && !ground_truth[i].empty()) {
            if (graph.adjacency[i][0] == ground_truth[i][0]) {
                accuracy += 1.0f;
            }
        }
        total++;
    }

    return accuracy / total;
}

// Основная функция для работы с обёрткой
class KNNWrapper {
public:
    KNNWrapper(bool use_gpu = false, bool debug = false)
        : graph(use_gpu, debug) {}

    bool load_data(const std::string& filename, int k) {
        return load_data(filename, graph, k);
    }

    void build_graph() {
        if (graph.is_gpu) {
            build_knn_graph_gpu(graph);
        } else {
            build_knn_graph_cpu(graph);
        }
    }

    void refine_graph(int subset_size) {
        refine_graph(graph, subset_size);
    }

    std::vector<int> query(const Point& query_point) {
        return query_knn(graph, query_point);
    }

    void save_graph(const std::string& filename) {
        save_graph(filename, graph);
    }

    void generate_ground_truth(std::vector<std::vector<int>>& ground_truth) {
        generate_ground_truth(graph, ground_truth);
    }

    float evaluate(const std::vector<std::vector<int>>& ground_truth) {
        return evaluate_knn(graph, ground_truth);
    }

    // Опции отладки
    void set_debug_mode(bool enabled) {
        graph.debug_mode = enabled;
    }

    bool get_debug_mode() const {
        return graph.debug_mode;
    }

private:
    KNNGraph graph;
};
```

