# Обёртка для KNN на GPU с использованием CUDA и C++

Вот структурированный подход к реализации обёртки для алгоритма k-ближайших соседей на GPU с использованием CUDA. Я предоставлю основные компоненты, которые можно расширять и адаптировать под ваши нужды.

## Основная структура проекта

```cpp
#include <vector>
#include <map>
#include <string>
#include <cuda_runtime.h>
#include <cuda_ml.h>
#include <iostream>
#include <fstream>
#include <algorithm>
#include <numeric>
#include <cmath>
#include <unordered_map>
#include <chrono>

// Константы и типы
const int MAX_K = 100;
const int MAX_DIM = 1000;
const int MAX_DATA_SIZE = 1000000; // Максимальный размер набора данных

struct Point {
    float* data; // Массив значений в пространстве
    int dim;     // Размерность
    int id;      // Идентификатор точки
};

struct KNNGraph {
    std::vector<Point> points; // Все точки в наборе данных
    std::vector<std::vector<int>> adjacency; // Граф соседей
    std::vector<float> distances; // Расстояния до соседей
    int k; // Количество ближайших соседей
    bool is_optimized; // Флаг оптимизации графа
    bool is_loaded; // Флаг загрузки графа
};

class KNNWrapper {
private:
    KNNGraph graph;
    std::vector<int> query_results; // Результаты запросов
    std::vector<int> query_indices; // Индексы запросов
    std::unordered_map<int, std::vector<int>> cached_results; // Кэш результатов

    // GPU ресурсы
    float* d_points; // GPU память для точек
    int* d_adjacency; // GPU память для графа
    float* d_distances; // GPU память для расстояний
    int* d_query_indices; // GPU память для индексов запросов
    int* d_k; // GPU память для k

    // Функции для GPU
    void allocate_gpu_memory();
    void free_gpu_memory();
    void build_graph_kernel(int n, int k);
    void query_kernel(int n, int query_idx, int* results, int* k);
    void optimize_graph_kernel(int n);

    // Поддержка отладки
    void print_graph_info();
    void print_query_result(int query_idx);

public:
    KNNWrapper();
    ~KNNWrapper();

    // Основные методы
    void load_data(const std::vector<Point>& points);
    void build_graph(int k);
    void optimize_graph();
    void save_graph(const std::string& filename);
    void load_graph(const std::string& filename);
    void reset_graph(int subset_size);
    void query(int query_idx, std::vector<int>& results);
    void query_batch(const std::vector<int>& query_indices, std::vector<std::vector<int>>& results);

    // Опции отладки
    void set_debug_mode(bool enable);
    bool get_debug_mode() const;
    void print_debug_info();
};
```

## Реализация основных методов

### Конструктор и деструктор

```cpp
KNNWrapper::KNNWrapper() : graph(), is_optimized(false), is_loaded(false) {
    allocate_gpu_memory();
}

KNNWrapper::~KNNWrapper() {
    free_gpu_memory();
}
```

### Загрузка данных

```cpp
void KNNWrapper::load_data(const std::vector<Point>& points) {
    graph.points = points;
    graph.k = MAX_K; // Установим максимальное значение k по умолчанию

    // Выделим память на GPU
    cudaMalloc(&d_points, points.size() * sizeof(float) * points[0].dim);
    cudaMalloc(&d_adjacency, points.size() * MAX_K * sizeof(int));
    cudaMalloc(&d_distances, points.size() * MAX_K * sizeof(float));

    // Копируем данные на GPU
    float* h_points = new float[points.size() * points[0].dim];
    for (size_t i = 0; i < points.size(); ++i) {
        std::copy(points[i].data, points[i].data + points[i].dim, h_points + i * points[i].dim);
    }
    cudaMemcpy(d_points, h_points, points.size() * points[0].dim * sizeof(float), cudaMemcpyHostToDevice);

    delete[] h_points;
    is_loaded = true;
}
```

### Построение графа

```cpp
void KNNWrapper::build_graph(int k) {
    if (!is_loaded) {
        std::cerr << "Граф не загружен. Загрузите данные сначала." << std::endl;
        return;
    }

    graph.k = k;
    graph.is_optimized = false;

    // Запускаем ядро на GPU
    int n = graph.points.size();
    build_graph_kernel<<<1, 1>>>(n, k);

    // Копируем результаты с GPU
    cudaMemcpy(&graph.adjacency[0][0], d_adjacency, n * k * sizeof(int), cudaMemcpyDeviceToHost);
    cudaMemcpy(&graph.distances[0], d_distances, n * k * sizeof(float), cudaMemcpyDeviceToHost);

    is_loaded = true;
    graph.is_optimized = false;
}
```

### Оптимизация графа

```cpp
void KNNWrapper::optimize_graph() {
    if (!is_loaded) {
        std::cerr << "Граф не загружен. Загрузите данные сначала." << std::endl;
        return;
    }

    // Запускаем ядро на GPU для оптимизации
    optimize_graph_kernel<<<1, 1>>>(graph.points.size());

    // Копируем обновленные данные с GPU
    cudaMemcpy(&graph.adjacency[0][0], d_adjacency, graph.points.size() * MAX_K * sizeof(int), cudaMemcpyDeviceToHost);
    cudaMemcpy(&graph.distances[0], d_distances, graph.points.size() * MAX_K * sizeof(float), cudaMemcpyDeviceToHost);

    graph.is_optimized = true;
}
```

### Запрос на графе

```cpp
void KNNWrapper::query(int query_idx, std::vector<int>& results) {
    if (!is_loaded) {
        std::cerr << "Граф не загружен. Загрузите данные сначала." << std::endl;
        return;
    }

    if (query_idx < 0 || query_idx >= graph.points.size()) {
        std::cerr << "Некорректный индекс запроса." << std::endl;
        return;
    }

    // Проверяем кэш
    if (cached_results.find(query_idx) != cached_results.end()) {
        results = cached_results[query_idx];
        return;
    }

    // Запускаем ядро на GPU
    int* h_results = new int[MAX_K];
    query_kernel<<<1, 1>>>(graph.points.size(), query_idx, h_results, &graph.k);

    // Копируем результаты с GPU
    results.resize(graph.k);
    for (int i = 0; i < graph.k; ++i) {
        results[i] = h_results[i];
    }

    // Сохраняем в кэш
    cached_results[query_idx] = results;

    delete[] h_results;
}
```

### Сохранение и загрузка графа

```cpp
void KNNWrapper::save_graph(const std::string& filename) {
    if (!is_loaded) {
        std::cerr << "Граф не загружен. Загрузите данные сначала." << std::endl;
        return;
    }

    std::ofstream out(filename, std::ios::binary);
    if (!out) {
        std::cerr << "Не удалось открыть файл для записи." << std::endl;
        return;
    }

    // Сохраняем размерности
    out.write(reinterpret_cast<const char*>(&graph.points[0].dim), sizeof(int));
    out.write(reinterpret_cast<const char*>(&graph.k), sizeof(int));

    // Сохраняем точки
    for (const auto& point : graph.points) {
        out.write(reinterpret_cast<const char*>(point.data), point.dim * sizeof(float));
        out.write(reinterpret_cast<const char*>(&point.id), sizeof(int));
    }

    // Сохраняем граф
    for (size_t i = 0; i < graph.points.size(); ++i) {
        for (int j = 0; j < graph.k; ++j) {
            out.write(reinterpret_cast<const char*>(&graph.adjacency[i][j]), sizeof(int));
            out.write(reinterpret_cast<const char*>(&graph.distances[i][j]), sizeof(float));
        }
    }

    out.close();
}

void KNNWrapper::load_graph(const std::string& filename) {
    std::ifstream in(filename, std::ios::binary);
    if (!in) {
        std::cerr << "Не удалось открыть файл для чтения." << std::endl;
        return;
    }

    // Читаем размерности
    int dim;
    in.read(reinterpret_cast<char*>(&dim), sizeof(int));
    int k;
    in.read(reinterpret_cast<char*>(&k), sizeof(int));

    // Читаем точки
    std::vector<Point> loaded_points;
    for (int i = 0; i < graph.points.size(); ++i) {
        Point point;
        point.dim = dim;
        point.id = i;
        in.read(reinterpret_cast<char*>(point.data), dim * sizeof(float));
        loaded_points.push_back(point);
    }

    // Читаем граф
    graph.k = k;
    graph.adjacency.resize(graph.points.size(), std::vector<int>(k));
    graph.distances.resize(graph.points.size() * k);

    for (size_t i = 0; i < graph.points.size(); ++i) {
        for (int j = 0; j < k; ++j) {
            in.read(reinterpret_cast<char*>(&graph.adjacency[i][j]), sizeof(int));
            in.read(reinterpret_cast<char*>(&graph.distances[i][j]), sizeof(float));
        }
    }

    in.close();
    graph.points = loaded_points;
    graph.is_loaded = true;
    graph.is_optimized = false;
}
```

### Сброс графа

```cpp
void KNNWrapper::reset_graph(int subset_size) {
    if (subset_size <= 0 || subset_size > graph.points.size()) {
        std::cerr << "Некорректный размер подмножества." << std::endl;
        return;
    }

    // Создаем новое подмножество
    std::vector<Point> subset;
    std::vector<int> indices(graph.points.size());
    std::iota(indices.begin(), indices.end(), 0);

    // Выбираем случайные индексы
    std::random_shuffle(indices.begin(), indices.end());
    for (int i = 0; i < subset_size; ++i) {
        subset.push_back(graph.points[indices[i]]);
    }

    // Загружаем новое подмножество
    load_data(subset);
    build_graph(graph.k);
}
```

## Ядра CUDA

### Построение графа

```cpp
__global__ void build_graph_kernel(int n, int k) {
    int idx = blockIdx.x * gridDim.x + threadIdx.x;

    if (idx >= n) return;

    // Здесь реализуется алгоритм построения графа k-ближайших соседей
    // Например, можно использовать алгоритм Ball Tree или Brute Force

    // Для простоты пока реализуем Brute Force
    for (int i = 0; i < n; ++i) {
        if (i == idx) continue;

        // Вычисляем расстояние между точками
        float distance = 0.0f;
        for (int d = 0; d < 100; ++d) { // Предполагаем размерность 100
            float diff = d_points[i * 100 + d] - d_points[idx * 100 + d];
            distance += diff * diff;
        }
        distance = sqrtf(distance);

        // Сохраняем ближайших k соседей
        if (distance < graph.distances[idx * k]) {
            // Перемещаем ближайшего соседа в начало списка
            for (int j = k - 1; j > 0; --j) {
                graph.adjacency[idx][j] = graph.adjacency[idx][j - 1];
                graph.distances[idx][j] = graph.distances[idx][j - 1];
            }
            graph.adjacency[idx][0] = i;
            graph.distances[idx][0] = distance;
        }
    }
}
```

### Оптимизация графа

```cpp
__global__ void optimize_graph_kernel(int n) {
    int idx = blockIdx.x * gridDim.x + threadIdx.x;

    if (idx >= n) return;

    // Здесь реализуется оптимизация графа (например, удаление дубликатов,
    // сортировка соседей по расстоянию, удаление слабых связей)

    // Пример: сортировка соседей по расстоянию
    for (int i = 0; i < k - 1; ++i) {
        for (int j = i + 1; j < k; ++j) {
            if (distances[idx * k + i] > distances[idx * k + j]) {
                // Обмен значениями
                int temp_adj = adjacency[idx][i];
                float temp_dist = distances[idx][i];

                adjacency[idx][i] = adjacency[idx][j];
                distances[idx][i] = distances[idx][j];

                adjacency[idx][j] = temp_adj;
                distances[idx][j] = temp_dist;
            }
        }
    }
}
