```cpp
__global__ void query_kernel(int n, int query_idx, int* results, int* k) {
    int idx = blockIdx.x * gridDim.x + threadIdx.x;

    if (idx >= n) return;

    // Если это запрос на саму себя, возвращаем пустой список
    if (idx == query_idx) {
        for (int i = 0; i < *k; ++i) {
            results[i] = -1; // Или другой маркер
        }
        return;
    }

    // Получаем ближайших соседей
    for (int i = 0; i < *k; ++i) {
        if (adjacency[idx][i] == query_idx) {
            results[i] = idx;
            break;
        }
    }
}
