import java.security.NoSuchAlgorithmException;
import java.util.List;
import java.util.Map;

public class ElasticsearchTransportBuilder {

    private final String[] hosts;
    private String username;
    private String password;
    private boolean disableHostnameVerification;
    private Map<String, String> headers;
    private int connectTimeout;
    private int socketTimeout;
    private boolean useSystemProxy;
    private String proxyHost;
    private int proxyPort;
    private String proxyUsername;
    private String proxyPassword;

    public ElasticsearchTransportBuilder(String... hosts) {
        this.hosts = hosts;
    }

    // Настройка прокси
    public ElasticsearchTransportBuilder withProxy(String host, int port) {
        this.proxyHost = host;
        this.proxyPort = port;
        return this;
    }

    public ElasticsearchTransportBuilder withProxy(String host, int port, String username, String password) {
        this.proxyHost = host;
        this.proxyPort = port;
        this.proxyUsername = username;
    }

    public ElasticsearchTransportBuilder disableProxy() {
        this.useSystemProxy = false;
        return this;
    }

    // Настройка аутентификации Elasticsearch
    public ElasticsearchTransportBuilder withCredentials(String username, String password) {
        this.username = username;
        this.password = password;
        return this;
    }

    // Настройка проверки сертификата
    public ElasticsearchTransportBuilder disableHostnameVerification() {
        this.disableHostnameVerification = true;
        return this;
    }

    // Настройка заголовков
    public ElasticsearchTransportBuilder withHeaders(Map<String, String> headers) {
        this.headers = headers;
        return this;
    }

    // Настройка тайм-аутов
    public ElasticsearchTransportBuilder withConnectTimeout(int timeout) {
        this.connectTimeout = timeout;
        return this;
    }

    public ElasticsearchTransportBuilder withSocketTimeout(int timeout) {
        this.socketTimeout = timeout;
        return this;
    }

    public RestHighLevelClient build() throws KeyManagementException, KeyStoreException, NoSuchAlgorithmException {
        // Создаем HTTP клиент с учетными данными Elasticsearch
        CredentialsProvider credentialsProvider = new BasicCredentialsProvider();
        if (username != null && password != null) {
            credentialsProvider.setCredentials(
                AuthScope.ANY,
                new UsernamePasswordCredentials(username, password)
            );
        }

        // Создаем HTTP клиент с прокси
        CloseableHttpClient httpClient;
        if (useSystemProxy) {
            httpClient = HttpClients.custom()
                .setProxy(new HttpHost(proxyHost, proxyPort))
                .setDefaultCredentialsProvider(credentialsProvider)
                .build();
        } else {
            httpClient = HttpClients.custom()
                .setDefaultCredentialsProvider(credentialsProvider)
                .build();
        }

        // Настройка SSL с отключенной проверкой хоста
        SSLContextBuilder sslContextBuilder = new SSLContextBuilder();
        if (!disableHostnameVerification) {
            sslContextBuilder.loadTrustMaterial(null, (certificate, authType) -> true);
        }

        SSLConnectionSocketFactory sslSocketFactory = new SSLConnectionSocketFactory(
            sslContextBuilder.build(),
            NoopHostnameVerifier.INSTANCE
        );

        // Создаем RestClient с настройками
        RestClientBuilder restClientBuilder = RestClient.builder(hosts)
            .setHttpClientConfigCallback(httpClientConfig -> {
                httpClientConfig.setDefaultCredentialsProvider(credentialsProvider);
                httpClientConfig.setSSLContext(sslContextBuilder.build());
                httpClientConfig.setSSLSocketFactory(sslSocketFactory);
                httpClientConfig.setDefaultHeaders(headers);
                httpClientConfig.setConnectTimeout(connectTimeout);
                httpClientConfig.setSocketTimeout(socketTimeout);
            });

        // Добавляем заголовки
        if (headers != null) {
            restClientBuilder.setDefaultHeaders(headers);
        }

        return new RestHighLevelClient(restClientBuilder);
    }
}
