// Функция для подсчета соседей
__device__ int countNeighbors(int x, int y, int width, int height) {
    int count = 0;
    for (int dx = -1; dx <= 1; dx++) {
        for (int dy = -1; dy <= 1; dy++) {
            if (dx == 0 && dy == 0) continue; // не считаем саму клетку

            int nx = x + dx;
            int ny = y + dy;

            // Проверка границ
            if (nx >= 0 && nx < width && ny >= 0 && ny < height) {
                count += grid[nx * width + ny];
            }
        }
    }
    return count;
}

// Ядро CUDA для обновления сетки
__global__ void golKernel(int* grid, int* newGrid, int width, int height) {
    int x = blockIdx.x * blockDim.x + threadIdx.x;
    int y = blockIdx.y * blockDim.y + threadIdx.y;

    if (x >= width || y >= height) return;

    int neighbors = countNeighbors(x, y, width, height);

    // Применяем правила Игры Жизни
    if (grid[x * width + y] == LIVE) {
        newGrid[x * width + y] = (neighbors == BIRTH || neighbors == SURVIVE) ? LIVE : DEAD;
    } else {
        newGrid[x * width + y] = (neighbors == BIRTH) ? LIVE : DEAD;
