    dim3 blockSize(16, 16);
    dim3 gridSize(width / blockSize.x + 1, height / blockSize.y + 1);

    // Основной цикл симуляции
    for (int iter = 0; iter < iterations; iter++) {
        // Запуск ядра
        golKernel<<<gridSize, blockSize>>>(d_grid, d_newGrid, width, height);

        // Копирование результата обратно на хост
        cudaMemcpy(h_newGrid.data(), d_newGrid, width * height * sizeof(int), cudaMemcpyDeviceToHost);

        // Преобразование в двумерный вектор
        std::vector<std::vector<int>> currentState(height, std::vector<int>(width));
        for (int y = 0; y < height; y++) {
            for (int x = 0; x < width; x++) {
                currentState[y][x] = h_newGrid[y * width + x];
            }
        }

        // Сохранение состояния (если тесты не прошли)
        if (runTests && iter % 10 == 0) {
            std::string filename = outputDir + "/iteration_" + std::to_string(iter) + ".png";
