    while (std::getline(config, line)) {
        size_t pos = line.find('=');
        if (pos != std::string::npos) {
            std::string key = line.substr(0, pos);
            std::string value = line.substr(pos + 1);
            configMap[key] = value;
        }
    }

    // Получение параметров из конфигурации
    int width = std::stoi(configMap["width"]);
    int height = std::stoi(configMap["height"]);
    int iterations = std::stoi(configMap["iterations"]);
    int numGPUs = std::stoi(configMap["numGPUs"]);
    std::string outputDir = configMap["outputDir"];

    // Создание тестовой сетки
    auto testGrid = createTestGrid(width, height);

    // Запуск симуляции на нескольких GPU
    for (int gpu = 0; gpu < numGPUs; gpu++) {
        int device = gpu;
        cudaSetDevice(device);

        // Аллокация памяти
        int* d_grid = nullptr;
        int* d_newGrid = nullptr;
        cudaMalloc(&d_grid, width * height * sizeof(int));
        cudaMalloc(&d_newGrid, width * height * sizeof(int));

        // Инициализация
        thrust::host_vector<int> h_grid(width * height);
        thrust::device_vector<int> d_grid_thrust(width * height);
        thrust::host_vector<int> h_newGrid(width * height);

        initGrid(h_grid.data(), width, height, testGrid);

        thrust::copy(h_grid.begin(), h_grid.end(), d_grid_thrust.begin());
        cudaMemcpy(d_grid, d_grid_thrust.data(), width * height * sizeof(int), cudaMemcpyDeviceToDevice);

        // Параметры блока
        dim3 blockSize(16, 16);
        dim3 gridSize(width / blockSize.x + 1, height / blockSize.y + 1);

        // Запуск тестов
        auto start = std::chrono::high_resolution_clock::now();

        for (int iter = 0; iter < iterations; iter++) {
            golKernel<<<gridSize, blockSize>>>(d_grid, d_newGrid, width, height);
            cudaMemcpy(h_newGrid.data(), d_newGrid, width * height * sizeof(int), cudaMemcpyDeviceToHost);

            // Обновление сетки
            thrust::copy(h_newGrid.begin(), h_newGrid.end(), d_grid_thrust.begin());
            cudaMemcpy(d_grid, d_grid_thrust.data(), width * height * sizeof(int), cudaMemcpyDeviceToDevice);
        }

        auto end = std::chrono::high_resolution_clock::now();
        auto duration = std::chrono::duration_cast<std::chrono::milliseconds>(end - start).count();

        std::cout << "GPU " << device << ": " << duration << " ms" << std::endl;

        // Сохранение результатов
        std::string filename = outputDir + "/gpu_" + std::to_string(device) + "_results.txt";
        std::ofstream out(filename);
        out << "GPU " << device << " performance: " << duration << " ms" << std::endl;
        out.close();

        // Очистка
        cudaFree(d_grid);
        cudaFree(d_newGrid);
    }
}
