## 4. Реализация – `src/knn_graph.cpp`

```cpp
#include "knn_graph.hpp"
#include <faiss/IndexIVFFlat.h>
#include <faiss/IndexIVFPQ.h>
#include <faiss/IndexIVFScalarQuantizer.h>
#include <faiss/gpu/GpuResources.h>
#include <faiss/gpu/StandardGpuResources.h>
#include <faiss/gpu/Cloner.h>
#include <faiss/utils/utils.h>

namespace knn {

// ---------------------------------------------------------------------------
// Helper for debug output
// ---------------------------------------------------------------------------
void KNNGraph::debug(const std::string& msg) const {
    if (cfg_.enable_debug) {
        std::cerr << "[KNNGraph] " << msg << std::endl;
    }
}

// ---------------------------------------------------------------------------
// Constructor
// ---------------------------------------------------------------------------
KNNGraph::KNNGraph(const Config& cfg) : cfg_(cfg) {
    if (cfg_.d <= 0) throw std::invalid_argument("Dimension d must be > 0");
    if (cfg_.k <= 0) throw std::invalid_argument("k must be > 0");
    if (!cfg_.use_gpu) {
        // fallback – CPU‑индекс (можно использовать для отладки)
        index_ = std::make_unique<faiss::IndexFlatL2>(cfg_.d);
    }
}

// ---------------------------------------------------------------------------
// Destructor
// ---------------------------------------------------------------------------
KNNGraph::~KNNGraph() {
    // FAISS‑объекты освобождаются автоматически через unique_ptr
}

// ---------------------------------------------------------------------------
// Загрузка данных из бинарного файла (float32)
// Формат: [float32 d0, float32 d1, …]  (N × d)
// Если transpose == true, файл хранит матрицу в виде [N][d] (row‑major)
// иначе – [d][N] (column‑major). Мы поддерживаем оба варианта.
// ---------------------------------------------------------------------------
void KNNGraph::load_data(const std::string& filename, bool transpose) {
    debug("Loading data from " + filename);
    // Считаем размер файла
    std::ifstream in(filename, std::ios::binary);
    if (!in) throw std::runtime_error("Cannot open file: " + filename);
    in.seekg(0, std::ios::end);
    size_t file_bytes = in.tellg();
    in.seekg(0, std::ios::beg);
    size_t expected = cfg_.d * n_total_ * sizeof(float);
    if (file_bytes != expected) {
        // Если размер не известен заранее, вычислим N = file_bytes/(d*sizeof(float))
        n_total_ = file_bytes / (cfg_.d * sizeof(float));
        debug("Detected N = " + std::to_string(n_total_));
    }

    // Выделяем буфер
    size_t buf_bytes = cfg_.d * n_total_ * sizeof(float);
    float* buf = new float[buf_bytes / sizeof(float)];
    in.read(reinterpret_cast<char*>(buf), file_bytes);
    in.close();

    // Если файл хранится в row‑major (каждая точка – блок из d чисел)
    // и пользователь указал transpose, то меняем порядок.
    if (transpose) {
        // Преобразуем в column‑major (FAISS ожидает column‑major)
        std::unique_ptr<float[]> transposed(new float[buf_bytes]);
        for (size_t i = 0; i < n_total_; ++i) {
            for (size_t j = 0; j < cfg_.d; ++j) {
                transposed[i * cfg_.d + j] = buf[j * n_total_ + i];
            }
        }
        delete[] buf;
        buf = transposed.release();
    }

    // Передаём данные в FAISS (FAISS копирует их в собственный буфер)
    xb_.reset(faiss::make_blob_ptr(buf, buf_bytes));
    debug("Data loaded: N=" + std::to_string(n_total_) + " d=" + std::to_string(cfg_.d));
}

// ---------------------------------------------------------------------------
// Построение индекса (GPU)
// ---------------------------------------------------------------------------
void KNNGraph::build_index() {
    debug("Building KNN index on GPU");
    assert(xb_ && "Data must be loaded before building index");

    // Параметры кластеризации (если используем IVF)
    // По умолчанию будем использовать IndexFlatL2 (полный перебор) – прост в реализации.
    // Для больших наборов можно переключиться на IVF/PQ, но здесь оставляем flat.
    if (cfg_.use_gpu) {
        // Создаём ресурсы GPU
        auto res = std::make_unique<faiss::gpu::StandardGpuResources>();
        // Выбираем тип индекса: flat (полный перебор) на GPU
        // Можно заменить на IVFFlat, IVFPQ и т.д.
        gpu_index_ = std::make_unique<faiss::gpu::IndexIVFFlat>(
            res.get(),
            cfg_.d,
            faiss::MetricType::L2,
            /*nlist=*/10,          // количество кластеров (можно менять)
            /*bits_per_code=*/8);  // не используется в flat‑режиме
        // Инициализируем параметры кластеризации
        gpu_index_->nprobe = cfg_.nprobe;
        // Копируем данные в GPU‑индекс
        gpu_index_->add(xb_.get(), n_total_);
        debug("GPU index built, nprobe=" + std::to_string(gpu_index_->nprobe));
    } else {
        // CPU‑fallback (для отладки)
        index_ = std::make_unique<faiss::IndexFlatL2>(cfg_.d);
        index_->add(xb_.get(), n_total_);
        debug("CPU index built, total vectors=" + std::to_string(index_->ntotal));
    }
}

// ---------------------------------------------------------------------------
// Уточнение (refinement) – пересчёт точного расстояния для top‑k кандидатов
// ---------------------------------------------------------------------------
void KNNGraph::refine(int64_t query_id,
                      int64_t top_k,
                      std::vector<float>& distances,
                      std::vector<int64_t>& indices) const {
    debug("Refining query " + std::to_string(query_id));
    assert(gpu_index_ && "Refine requires GPU index");
    // 1) Получаем `top_k * 5` кандидатов (чтобы потом отфильтровать)
    const int64_t k_candidate = std::max<int64_t>(top_k * 5, 10);
    std::vector<int64_t> cand_idx(k_candidate);
    std::vector<float> cand_dist(k_candidate);
    // Запрос на GPU‑индексе
    gpu_index_->search(
        reinterpret_cast<const float*>(xb_.get()) + query_id * dim_,
        k_candidate,
        cand_dist.data(),
        cand_idx.data());

    // 2) Сортируем и берём первые `top_k`
    std::vector<std::pair<float, int64_t>> sorted;
    sorted.reserve(k_candidate);
    for (int64_t i = 0; i < k_candidate; ++i) {
        sorted.emplace_back(cand_dist[i], cand_idx[i]);
    }
    std::sort(sorted.begin(), sorted.end(),
              [](const auto& a, const auto& b) { return a.first < b.first; });
    sorted.resize(top_k);

    // 3) Пересчитываем точные расстояния (L2) для отобранных кандидатов
    distances.clear();
    indices.clear();
    distances.reserve(top_k);
    indices.reserve(top_k);
    for (const auto& p : sorted) {
        // Вычисляем точное L2 между query и кандидатом
        const float* q = reinterpret_cast<const float*>(xb_.get()) + query_id * dim_;
        const float* c = reinterpret_cast<const float*>(xb_.get()) + p.second * dim_;
        float dist = 0.0f;
        for (size_t i = 0; i < cfg_.d; ++i) {
            float diff = q[i] - c[i];
            dist += diff * diff;
        }
        distances.push_back(std::sqrt(dist));
        indices.push_back(p.second);
    }
}

// ---------------------------------------------------------------------------
// Выполнение запроса KNN (прямой поиск через GPU‑индекс)
// ---------------------------------------------------------------------------
void KNNGraph::query(int64_t query_id,
                     int64_t top_k,
                     std::vector<float>& distances,
                     std::vector<int64_t>& indices) const {
    debug("Querying id=" + std::to_string(query_id) + " k=" + std::to_string(top_k));
    assert(gpu_index_ && "Query requires GPU index");
    // FAISS возвращает массив расстояний и индексов
    std::vector<float> faiss_dist(top_k);
    std::vector<int64_t> faiss_idx(top_k);
    gpu_index_->search(
        reinterpret_cast<const float*>(xb_.get()) + query_id * dim_,
        top_k,
        faiss_dist.data(),
        faiss_idx.data());

    // При необходимости можно выполнить уточнение (refine) для повышения точности
    // (особенно если использовался IVF с небольшим nprobe)
    // Здесь просто копируем результат.
    distances = std::move(faiss_dist);
    indices   = std::move(faiss_idx);
}

// ---------------------------------------------------------------------------
// Сохранить граф в файл (binary)
// Формат: [magic][version][N][d][xb (float32)][index metadata]
// ---------------------------------------------------------------------------
void KNNGraph::save_graph(const std::string& filename) const {
    debug("Saving graph to " + filename);
    // Сохраняем только базу точек (xb_) – индекс можно восстановить заново
    std::ofstream out(filename, std::ios::binary);
    out.write(reinterpret_cast<const char*>(xb_.get()),
              cfg_.d * n_total_ * sizeof(float));
    out.close();
    debug("Graph saved");
}

// ---------------------------------------------------------------------------
// Загрузить граф из файла (перезаписывает текущий объект)
// ---------------------------------------------------------------------------
void KNNGraph::load_graph(const std::string& filename) {
    debug("Loading graph from " + filename);
    std::ifstream in(filename, std::ios::binary);
    if (!in) throw std::runtime_error("Cannot open graph file");
    // Читаем размер файла и восстанавливаем N и d
    in.seekg(0, std::ios::end);
    size_t file_bytes = in.tellg();
    in.seekg(0, std::ios::beg);
    size_t expected = cfg_.d * n_total_ * sizeof(float);
    if (file_bytes != expected) {
        // Если размер не совпадает, пересчитываем N
        n_total_ = file_bytes / (cfg_.d * sizeof(float));
        debug("Detected N=" + std::to_string(n_total_) + " while loading graph");
    }
    // Выделяем буфер и читаем данные
    size_t buf_bytes = cfg_.d * n_total_ * sizeof(float);
    float* buf = new float[buf_bytes / sizeof(float)];
    in.read(reinterpret_cast<char*>(buf), file_bytes);
    in.close();
    xb_.reset(faiss::make_blob_ptr(buf, buf_bytes));
    debug("Graph loaded");
}

// ---------------------------------------------------------------------------
// Сбросить граф до подмножества фиксированного размера
// ---------------------------------------------------------------------------
void KNNGraph::reset_to_subset(int64_t new_size) {
    if (new_size > n_total_) {
        throw std::invalid_argument("new_size exceeds original size");
    }
    n_total_ = new_size;
    debug("Reset graph to subset size " + std::to_string(new_size));
    // Пересоздаём xb_ с новым размером
    size_t new_bytes = cfg_.d * n_total_ * sizeof(float);
    float* new_buf = new float[new_bytes / sizeof(float)];
    // Копируем старые данные (если они есть)
    if (xb_) {
        size_t copy_bytes = std::min(new_bytes, xb_.get_blob_bytes());
        std::memcpy(new_buf, xb_.get(), copy_bytes);
    }
    xb_.reset(faiss::make_blob_ptr(new_buf, new_bytes));
    // При необходимости пересоздаём индекс (или оставляем старый, но он будет
    // работать только с новым размером)
    if (gpu_index_) {
        gpu_index_->reset(xb_.get(), n_total_);
    }
    if (index_) {
        index_->reset(xb_.get(), n_total_);
    }
}

// ---------------------------------------------------------------------------
// Пример вспомогательной функции для измерения времени
// ---------------------------------------------------------------------------
template <class F>
double measure_ms(F&& f) {
    auto t0 = std::chrono::high_resolution_clock::now();
    f();
    auto t1 = std::chrono::high_resolution_clock::now();
    std::chrono::duration<double, std::milli> dur = t1 - t0;
    return dur.count();
}

} // namespace knn
