## 5. Тестовый драйвер – `src/main.cpp`

```cpp
#include "knn_graph.hpp"
#include <random>
#include <chrono>
#include <iostream>
#include <iomanip>

using namespace knn;

/* --------------------------------------------------------------
   Утилита: генерация случайного набора точек (float32)
   -------------------------------------------------------------- */
static std::vector<float> generate_random_points(size_t N, size_t d, float seed = 1234.0f) {
    std::mt19937_64 rng(static_cast<unsigned long>(seed));
    std::normal_distribution<float> dist(0.0f, 1.0f);
    std::vector<float> data(N * d);
    for (size_t i = 0; i < N * d; ++i) data[i] = dist(rng);
    return data;
}

/* --------------------------------------------------------------
   Запись точек в бинарный файл (float32, row‑major)
   -------------------------------------------------------------- */
static void write_binary(const std::string& filename,
                         const std::vector<float>& points,
                         size_t d) {
    std::ofstream out(filename, std::ios::binary);
    out.write(reinterpret_cast<const char*>(points.data()),
              points.size() * sizeof(float));
}

/* --------------------------------------------------------------
   Чтение точек из бинарного файла
   -------------------------------------------------------------- */
static std::vector<float> read_binary(const std::string& filename) {
    std::ifstream in(filename, std::ios::binary);
    in.seekg(0, std::ios::end);
    size_t bytes = in.tellg();
    in.seekg(0, std::ios::beg);
    size_t N = bytes / sizeof(float); // d будет известен из конфига
    std::vector<float> buf(bytes / sizeof(float));
    in.read(reinterpret_cast<char*>(buf.data()), bytes);
    return buf;
}

/* --------------------------------------------------------------
   main()
   -------------------------------------------------------------- */
int main(int argc, char** argv) {
    // ------------------- Параметры -------------------
    Config cfg{};
    cfg.d = 128;                 // размерность
    cfg.k = 10;                  // количество соседей
    cfg.nprobe = 10;             // для IVF (можно менять)
    cfg.use_gpu = true;          // включаем GPU
    cfg.gpu_id = 0;
    cfg.enable_debug = true;     // подробный лог

    KNNGraph graph(cfg);

    // ------------------- Генерация/загрузка данных -------------------
    const size_t N = 1'000'000;  // 1 млн точек
    std::cout << "Generating " << N << " vectors of dimension " << cfg.d << " ...\n";
    auto points = generate_random_points(N, cfg.d);
    write_binary("data/points.bin", points, cfg.d);
    graph.load_data("data/points.bin", false); // row‑major → column‑major внутри

    // ------------------- Построение индекса -------------------
    double build_time = measure_ms([&]{
        graph.build_index();
    });
    std::cout << std::fixed << std::setprecision(2)
              << "Index built in " << build_time << " ms\n";

    // ------------------- Оценка качества (Recall@k) -------------------
    // Для небольшого подмножества (например, 10 000 точек) построим эталон
    const size_t eval_subset = 10'000;
    graph.reset_to_subset(eval_subset);
    std::cout << "Running evaluation on subset of size " << eval_subset << "\n";

    // Создаём эталонный индекс (CPU, полный перебор)
    faiss::IndexFlatL2 cpu_index(cfg.d);
    cpu_index.add(graph.xb_.get(), eval_subset);
    // Запросы: случайные 1000 точек из подмножества
    std::mt19937 rng(42);
    std::uniform_int_distribution<size_t> uni(0, eval_subset - 1);
    size_t n_queries = 1'000;
    double recall_sum = 0.0;
    double query_time_sum = 0.0;
    for (size_t i = 0; i < n_queries; ++i) {
        size_t qid = uni(rng);
        std::vector<float> dists;
        std::vector<int64_t> idxs;
        double qtime = measure_ms([&]{
            graph.query(static_cast<int64_t>(qid), cfg.k, dists, idxs);
        });
        query_time_sum += qtime;

        // Получаем истинные соседи от CPU‑индекса
        std::vector<float> true_dists(eval_subset);
        std::vector<int64_t> true_idx(eval_subset);
        cpu_index.search(
            reinterpret_cast<const float*>(graph.xb_.get()) + qid * cfg.d,
            cfg.k,
            true_dists.data(),
            true_idx.data());

        // Сравниваем: сколько найденных индексов присутствует в true_idx
        std::unordered_set<int64_t> true_set(true_idx.begin(), true_idx.end());
        int64_t hits = 0;
        for (int64_t id : idxs) if (true_set.count(id)) ++hits;
        recall_sum += static_cast<double>(hits) / cfg.k;
    }
    double avg_recall = recall_sum / n_queries;
    double avg_query_ms = query_time_sum / n_queries;
    std::cout << "Recall@k = " << avg_recall << " (average over " << n_queries << " queries)\n";
    std::cout << "Avg query time = " << avg_query_ms << " ms\n";

    // ------------------- Пример запроса -------------------
    std::cout << "\n--- Example query ---\n";
    size_t query_id = 12345 % eval_subset;
    std::vector<float> q_dists;
    std::vector<int64_t> q_idx;
    double qtime = measure_ms([&]{
        graph.query(static_cast<int64_t>(query_id), cfg.k, q_dists, q_idx);
    });
    std::cout << "Query #" << query_id << " took " << qtime << " ms\n";
    std::cout << "Top-" << cfg.k << " neighbors:\n";
    for (size_t i = 0; i < cfg.k; ++i) {
        std::cout << "  [" << i << "] idx=" << q_idx[i]
                  << " dist=" << std::fixed << std::setprecision(4) << q_dists[i] << "\n";
    }

    // ------------------- Сохранение/загрузка графа -------------------
    std::cout << "\nSaving graph to 'graph.bin' ...\n";
    graph.save_graph("graph.bin");
    std::cout << "Now loading it back ...\n";
    KNNGraph graph2(cfg);
    graph2.load_graph("graph.bin");
    std::cout << "Graph re‑loaded, ready for further queries.\n";

    // ------------------- Сброс до подмножества -------------------
    std::cout << "\nResetting graph to first 5000 points ...\n";
    graph.reset_to_subset(5000);
    std::cout << "New size = " << graph.config().d * graph.n_total_ << " floats stored.\n";

    return 0;
}
