## 5. Тестовый драйвер – `src/main.cpp`

```cpp
#include "knn_graph.hpp"
#include <iostream>
#include <chrono>
#include <random>
#include <cassert>

using namespace knn;

/* -------------------------------------------------------------
   Вспомогательная функция: генерирует случайный набор float32
   ------------------------------------------------------------- */
static std::vector<float> generate_random_data(int64_t n, int64_t d,
                                               float scale = 1.0f) {
    std::mt19937_64 rng(12345);
    std::normal_distribution<float> dist(0.0f, 1.0f);
    std::vector<float> data(n * d);
    for (auto& v : data) v = dist(rng) * scale;
    return data;
}

/* -------------------------------------------------------------
   Чтение бинарного файла в вектор<float>
   ------------------------------------------------------------- */
static std::vector<float> read_binary(const std::string& path) {
    std::ifstream in(path, std::ios::binary);
    in.seekg(0, std::ios::end);
    size_t sz = in.tellg();
    in.seekg(0);
    std::vector<float> buf(sz / sizeof(float));
    in.read(reinterpret_cast<char*>(buf.data()), sz);
    return buf;
}

/* -------------------------------------------------------------
   Запись вектора в бинарный файл
   ------------------------------------------------------------- */
static void write_binary(const std::string& path,
                         const std::vector<float>& buf) {
    std::ofstream out(path, std::ios::binary);
    out.write(reinterpret_cast<const char*>(buf.data()), buf.size()*sizeof(float));
}

/* -------------------------------------------------------------
   main
   ------------------------------------------------------------- */
int main(int argc, char** argv) {
    // ------------------- Параметры -------------------
    Config cfg{};
    cfg.d        = 128;          // размерность
    cfg.k        = 10;           // количество соседей
    cfg.nprobe   = 10;           // промы для IVF
    cfg.use_gpu  = true;        // включаем GPU
    cfg.gpu_id   = 0;            // первый GPU
    cfg.enable_debug = false;   // можно включить через env/флаг

    // ------------------- Создаём объект -------------------
    KNNGraph graph(cfg);

    // ------------------- Генерация тестовых данных -------------------
    const int64_t N = 100'000;   // размер базы
    const int64_t D = cfg.d;
    auto base_data = generate_random_data(N, D);
    // Сохраняем в файл (можно переиспользовать)
    const std::string db_path = "data/sample.bin";
    write_binary(db_path, base_data);
    std::cout << "[main] Сгенерировано " << N << " векторов, записано в " << db_path << "\n";

    // ------------------- Загрузка (или построение) -------------------
    if (!graph.load(db_path)) {
        std::cout << "[main] Индекс не найден – строим с нуля\n";
        graph.build(base_data.data(), N);
        graph.save(db_path);
    } else {
        std::cout << "[main] Индекс загружен из " << db_path << "\n";
    }

    // ------------------- Пример запроса -------------------
    // Выбираем случайный вектор из базы как запрос
    std::mt19937_64 rng(98765);
    std::uniform_int_distribution<int64_t> uni(0, N-1);
    int64_t qid = uni(rng);
    const float* query_vec = base_data.data() + qid * D;

    auto start = std::chrono::high_resolution_clock::now();
    auto neighbors = graph.query(query_vec, cfg.k);
    auto stop = std::chrono::high_resolution_clock::now();
    std::chrono::duration<double> elapsed = stop - start;
    std::cout << "[main] Время поиска: " << elapsed.count() << " сек.\n";

    std::cout << "Top-" << cfg.k << " соседей:\n";
    for (size_t i = 0; i < neighbors.size(); ++i) {
        std::cout << "  [" << i << "] id=" << neighbors[i].first
                  << " dist=" << std::fixed << std::setprecision(4)
                  << neighbors[i].second << "\n";
    }

    // ------------------- Оценка (если есть ground‑truth) -------------------
    // Для простоты построим эталонный ground‑truth через CPU‑индекс с
    // большим nprobe (например, 100) и сравним recall.
    // (В реальном проекте ground‑truth хранится отдельно.)
    // Здесь просто демонстрируем вызов `evaluate`.
    /*
    std::vector<std::vector<std::pair<int64_t,float>>> gt(N);
    // заполняем gt[i] – истинные соседи i‑го вектора (можно вычислить полным перебором)
    // ...
    float recall = 0, mrr = 0;
    graph.evaluate(gt, recall, mrr);
    std::cout << "Recall@" << cfg.k << " = " << recall << ", MRR = " << mrr << "\n";
    */

    // ------------------- Сброс до подмножества -------------------
    // Например, оставим только первые 20 000 векторов
    std::cout << "[main] Сбрасываем граф до 20 000 векторов\n";
    graph.reset(20'000);
    // После reset можно снова выполнить запрос и измерить время
    auto start2 = std::chrono::high_resolution_clock::now();
    auto neighbors2 = graph.query(query_vec, cfg.k);
    auto stop2 = std::chrono::high_resolution_clock::now();
    std::chrono::duration<double> elapsed2 = stop2 - start2;
    std::cout << "[main] Время поиска после reset: " << elapsed2.count() << " сек.\n";

    return 0;
}
