## 🔹 2. `knn_wrapper.cu` — Реализация с CUDA

```cpp
#include "knn_wrapper.h"
#include <iostream>
#include <fstream>
#include <sstream>
#include <algorithm>
#include <cmath>
#include <cub/cub.cuh>

// Ядро: вычисление евклидова расстояния между двумя векторами
__device__ float computeDistance(const float* a, const float* b, int dim) {
    float dist = 0.0f;
    for (int i = 0; i < dim; ++i) {
        float diff = a[i] - b[i];
        dist += diff * diff;
    }
    return sqrtf(dist);
}

// Ядро: построение KNN для одной точки
__global__ void knnKernel(
    const float* data,
    int* graph,
    float* graph_dist,
    int num_points,
    int dim,
    int k
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    const float* point = data + idx * dim;
    std::pair<float, int> neighbors[k];
    for (int i = 0; i < k; ++i) {
        neighbors[i] = {INFINITY, -1};
    }

    for (int j = 0; j < num_points; ++j) {
        if (j == idx) continue;
        float dist = computeDistance(point, data + j * dim, dim);

        // Вставка в отсортированный массив
        for (int i = 0; i < k; ++i) {
            if (dist < neighbors[i].first) {
                for (int l = k - 1; l > i; --l) {
                    neighbors[l] = neighbors[l - 1];
                }
                neighbors[i] = {dist, j};
                break;
            }
        }
    }

    for (int i = 0; i < k; ++i) {
        graph[idx * k + i] = neighbors[i].second;
        graph_dist[idx * k + i] = neighbors[i].first;
    }
}

// Ядро: проверка mutual KNN
__global__ void mutualKNNKernel(
    const int* graph,
    int* refined_graph,
    int num_points,
    int k
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    int count = 0;
    for (int i = 0; i < k; ++i) {
        int neighbor = graph[idx * k + i];
        if (neighbor == -1) continue;

        bool mutual = false;
        for (int j = 0; j < k; ++j) {
            if (graph[neighbor * k + j] == idx) {
                mutual = true;
                break;
            }
        }
        if (mutual) {
            refined_graph[idx * k + count++] = neighbor;
        }
    }
    // Заполнить оставшиеся -1
    for (int i = count; i < k; ++i) {
        refined_graph[idx * k + i] = -1;
    }
}

// Реализация методов

KNNWrapper::KNNWrapper(int dim, int k, bool debug)
    : dim_(dim), k_(k), num_points_(0), debug_(debug) {}

KNNWrapper::~KNNWrapper() {
    freeDeviceMemory();
}

void KNNWrapper::printDebug(const std::string& msg) {
    if (debug_) {
        std::cout << "[DEBUG] " << msg << std::endl;
    }
}

bool KNNWrapper::loadDataset(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        printDebug("Failed to open dataset file.");
        return false;
    }

    std::vector<std::vector<float>> temp_data;
    std::string line;
    while (std::getline(file, line)) {
        std::stringstream ss(line);
        std::vector<float> vec;
        float val;
        while (ss >> val) {
            vec.push_back(val);
        }
        if (vec.size() == dim_) {
            temp_data.push_back(vec);
        }
    }
    file.close();

    num_points_ = temp_data.size();
    h_data_.resize(num_points_ * dim_);
    for (int i = 0; i < num_points_; ++i) {
        for (int j = 0; j < dim_; ++j) {
            h_data_[i * dim_ + j] = temp_data[i][j];
        }
    }

    printDebug("Dataset loaded: " + std::to_string(num_points_) + " points.");
    return true;
}

bool KNNWrapper::allocateDeviceMemory() {
    freeDeviceMemory();

    cudaError_t err;
    err = cudaMalloc(&d_data_, num_points_ * dim_ * sizeof(float));
    if (err != cudaSuccess) { printDebug("cudaMalloc d_data_ failed"); return false; }

    err = cudaMalloc(&d_graph_, num_points_ * k_ * sizeof(int));
    if (err != cudaSuccess) { printDebug("cudaMalloc d_graph_ failed"); return false; }

    err = cudaMalloc(&d_graph_dist_, num_points_ * k_ * sizeof(float));
    if (err != cudaSuccess) { printDebug("cudaMalloc d_graph_dist_ failed"); return false; }

    return true;
}

void KNNWrapper::freeDeviceMemory() {
    if (d_data_) { cudaFree(d_data_); d_data_ = nullptr; }
    if (d_graph_) { cudaFree(d_graph_); d_graph_ = nullptr; }
    if (d_graph_dist_) { cudaFree(d_graph_dist_); d_graph_dist_ = nullptr; }
}

bool KNNWrapper::computeKNNOnGPU() {
    if (!allocateDeviceMemory()) return false;

    cudaMemcpy(d_data_, h_data_.data(), num_points_ * dim_ * sizeof(float), cudaMemcpyHostToDevice);

    dim3 block(256);
    dim3 grid((num_points_ + block.x - 1) / block.x);

    knnKernel<<<grid, block>>>(
        d_data_, d_graph_, d_graph_dist_,
        num_points_, dim_, k_
    );

    cudaDeviceSynchronize();

    h_graph_.resize(num_points_ * k_);
    h_graph_dist_.resize(num_points_ * k_);
    cudaMemcpy(h_graph_.data(), d_graph_, num_points_ * k_ * sizeof(int), cudaMemcpyDeviceToHost);
    cudaMemcpy(h_graph_dist_.data(), d_graph_dist_, num_points_ * k_ * sizeof(float), cudaMemcpyDeviceToHost);

    printDebug("KNN graph built on GPU.");
    return true;
}

bool KNNWrapper::buildGraph() {
    if (num_points_ == 0) {
        printDebug("No data loaded.");
        return false;
    }
    return computeKNNOnGPU();
}

bool KNNWrapper::refineGraph() {
    if (h_graph_.empty()) {
        printDebug("Graph not built yet.");
        return false;
    }

    std::vector<int> refined_graph(num_points_ * k_, -1);
    int* d_refined_graph;
    cudaMalloc(&d_refined_graph, num_points_ * k_ * sizeof(int));

    dim3 block(256);
    dim3 grid((num_points_ + block.x - 1) / block.x);

    mutualKNNKernel<<<grid, block>>>(
        h_graph_.data(), d_refined_graph, num_points_, k_
    );

    cudaDeviceSynchronize();

    cudaMemcpy(refined_graph.data(), d_refined_graph, num_points_ * k_ * sizeof(int), cudaMemcpyDeviceToHost);
    cudaFree(d_refined_graph);

    h_graph_ = std::move(refined_graph);
    printDebug("Graph refined using mutual KNN.");
    return true;
}

bool KNNWrapper::computeGroundTruthCPU(int gt_k) {
    h_gt_graph_.resize(num_points_ * gt_k, -1);
    h_gt_dist_.resize(num_points_ * gt_k, INFINITY);

    for (int i = 0; i < num_points_; ++i) {
        const float* point = h_data_.data() + i * dim_;
        std::vector<std::pair<float, int>> neighbors(gt_k, {INFINITY, -1});

        for (int j = 0; j < num_points_; ++j) {
            if (i == j) continue;
            float dist = 0.0f;
            for (int d = 0; d < dim_; ++d) {
                float diff = point[d] - h_data_[j * dim_ + d];
                dist += diff * diff;
            }
            dist = sqrtf(dist);

            for (int k = 0; k < gt_k; ++k) {
                if (dist < neighbors[k].first) {
                    for (int l = gt_k - 1; l > k; --l) {
                        neighbors[l] = neighbors[l - 1];
                    }
                    neighbors[k] = {dist, j};
                    break;
                }
            }
        }

        for (int k = 0; k < gt_k; ++k) {
            h_gt_graph_[i * gt_k + k] = neighbors[k].second;
            h_gt_dist_[i * gt_k + k] = neighbors[k].first;
        }
    }

    printDebug("Ground truth computed on CPU.");
    return true;
}

bool KNNWrapper::generateGroundTruth(int gt_k) {
    if (num_points_ == 0) return false;
    return computeGroundTruthCPU(gt_k);
}

void KNNWrapper::evaluateGraph(int eval_k, double& precision, double& recall) {
    if (h_gt_graph_.empty()) {
        printDebug("Ground truth not generated.");
        precision = recall = 0.0;
        return;
    }

    int correct = 0;
    int total = 0;

    for (int i = 0; i < num_points_; ++i) {
        std::vector<int> gt_set(eval_k);
        std::vector<int> pred_set(eval_k);

        for (int j = 0; j < eval_k; ++j) {
            gt_set[j] = h_gt_graph_[i * eval_k + j];
            pred_set[j] = h_graph_[i * k_ + j];
        }

        // Подсчёт пересечений
        int matches = 0;
        for (int g : gt_set) {
            if (g == -1) continue;
            for (int p : pred_set) {
                if (p == g) {
                    matches++;
                    break;
                }
            }
        }

        correct += matches;
        total += eval_k;
    }

    precision = (total > 0) ? static_cast<double>(correct) / total : 0.0;
    recall = (total > 0) ? static_cast<double>(correct) / total : 0.0; // Здесь precision == recall, так как ground truth фиксирован

    printDebug("Evaluation: Precision@" + std::to_string(eval_k) + " = " + std::to_string(precision));
}

KNNQueryResult KNNWrapper::query(const std::vector<float>& query_vec, int k) {
    KNNQueryResult result;
    result.indices.resize(k, -1);
    result.distances.resize(k, INFINITY);

    if (query_vec.size() != dim_) return result;

    const float* q = query_vec.data();
    for (int i = 0; i < num_points_; ++i) {
        float dist = 0.0f;
        for (int d = 0; d < dim_; ++d) {
            float diff = q[d] - h_data_[i * dim_ + d];
            dist += diff * diff;
        }
        dist = sqrtf(dist);

        for (int j = 0; j < k; ++j) {
            if (dist < result.distances[j]) {
                for (int l = k - 1; l > j; --l) {
                    result.distances[l] = result.distances[l - 1];
                    result.indices[l] = result.indices[l - 1];
                }
                result.distances[j] = dist;
                result.indices[j] = i;
                break;
            }
        }
    }

    return result;
}

std::vector<KNNQueryResult> KNNWrapper::batchQuery(const std::vector<std::vector<float>>& queries, int k) {
    std::vector<KNNQueryResult> results;
    for (const auto& q : queries) {
        results.push_back(query(q, k));
    }
    return results;
}

bool KNNWrapper::saveGraph(const std::string& filename) {
    std::ofstream file(filename);
    if (!file.is_open()) return false;

    file << dim_ << " " << k_ << " " << num_points_ << "\n";
    for (int i = 0; i < num_points_; ++i) {
        for (int j = 0; j < k_; ++j) {
            file << h_graph_[i * k_ + j] << " " << h_graph_dist_[i * k_ + j] << " ";
        }
        file << "\n";
    }
    file.close();
    printDebug("Graph saved to " + filename);
    return true;
}

bool KNNWrapper::loadGraph(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) return false;

    int dim, k, n;
    file >> dim >> k >> n;
    if (dim != dim_ || k != k_) {
        file.close();
        printDebug("Graph dimensions do not match.");
        return false;
    }

    num_points_ = n;
    h_graph_.resize(n * k);
    h_graph_dist_.resize(n * k);

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            file >> h_graph_[i * k + j] >> h_graph_dist_[i * k + j];
        }
    }
    file.close();

    printDebug("Graph loaded from " + filename);
    return true;
}

bool KNNWrapper::resetGraphSubset(const std::vector<int>& indices) {
    if (indices.empty()) return true;

    std::vector<int> new_graph(num_points_ * k_, -1);
    std::vector<float> new_dist(num_points_ * k_, INFINITY);

    for (int i = 0; i < num_points_; ++i) {
        if (std::find(indices.begin(), indices.end(), i) != indices.end()) {
            // Сброс: заполняем -1
            for (int j = 0; j < k_; ++j) {
                new_graph[i * k_ + j] = -1;
                new_dist[i * k_ + j] = INFINITY;
            }
        } else {
            // Копируем старые значения
            for (int j = 0; j < k_; ++j) {
                new_graph[i * k_ + j] = h_graph_[i * k_ + j];
                new_dist[i * k_ + j] = h_graph_dist_[i * k_ + j];
            }
        }
    }

    h_graph_ = std::move(new_graph);
    h_graph_dist_ = std::move(new_dist);
    printDebug("Graph reset for " + std::to_string(indices.size()) + " indices.");
    return true;
}
