#include "knn_wrapper.h"
#include <iostream>

int main() {
    KNNWrapper knn(3, 5, true);  // 3D, k=5, debug

    if (!knn.loadDataset("data.txt")) {
        std::cerr << "Failed to load dataset.\n";
        return -1;
    }

    if (!knn.buildGraph()) {
        std::cerr << "Failed to build graph.\n";
        return -1;
    }

    if (!knn.refineGraph()) {
        std::cerr << "Refinement failed.\n";
    }

    if (!knn.generateGroundTruth(5)) {
        std::cerr << "Ground truth failed.\n";
    }

    double prec, rec;
    knn.evaluateGraph(5, prec, rec);

    // Запрос
    std::vector<float> query = {1.0f, 2.0f, 3.0f};
    auto result = knn.query(query, 3);
    std::cout << "Query result: ";
    for (int i = 0; i < 3; ++i) {
        std::cout << result.indices[i] << "(" << result.distances[i] << ") ";
    }
    std::cout << "\n";

    // Сохранение и загрузка
    knn.saveGraph("graph.bin");
    KNNWrapper knn2(3, 5);
    knn2.loadGraph("graph.bin");

