  console.log('Копирование статических файлов...');
  const assetsSrc = path.join(SRC_DIR, 'assets');
  const assetsDest = path.join(PUBLIC_DIR, 'assets');

  if (fs.existsSync(assetsSrc)) {
    fs.cpSync(assetsSrc, assetsDest, { recursive: true });
    console.log(`Ресурсы скопированы в ${assetsDest}`);
  } else {
    console.log('Каталог assets не найден — пропускаем копирование.');
  }
}

// Очистка
