```cpp
void initialize_glider(unsigned char* grid, int width, int height, int pitch_bytes) {
    int center_x = width / 2;
    int center_y = height / 2;

    unsigned char* ptr = (unsigned char*)((char*)grid + center_y * pitch_bytes) + center_x;
    *ptr = 1;

    ptr = (unsigned char*)((char*)grid + (center_y - 1) * pitch_bytes) + (center_x + 1);
    *ptr = 1;

    ptr = (unsigned char*)((char*)grid + (center_y + 1) * pitch_bytes) + (center_x + 1);
    *ptr = 1;

    ptr = (unsigned char*)((char*)grid + (center_y + 1) * pitch_bytes) + center_x;
    *ptr = 1;

    ptr = (unsigned char*)((char*)grid + (center_y) * pitch_bytes) + (center_x - 1);
    *ptr = 1;
}
