```cpp
#include <fstream>
#include <sstream>

void save_grid_to_ppm(const unsigned char* grid, int width, int height, int pitch_bytes, const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        fprintf(stderr, "Failed to open file: %s\n", filename.c_str());
        return;
    }

    file << "P6\n" << width << " " << height << "\n255\n";

    for (int y = 0; y < height; y++) {
        const unsigned char* row = (const unsigned char*)((const char*)grid + y * pitch_bytes);
        for (int x = 0; x < width; x++) {
            unsigned char val = row[x] ? 255 : 0;
            file.put(val); file.put(val); file.put(val); // RGB
        }
    }

    file.close();
}
