## ✅ 3. Основная логика на хосте (с поддержкой нескольких GPU)

```cpp
// main.cu
#include <mpi.h>
#include <cuda_runtime.h>
#include <iostream>
#include <vector>
#include <string>
#include <cstring>

#define BLOCK_SIZE 16

void run_game_of_life(int world_size, int rank, int width, int height, int steps) {
    int local_height = height / world_size;
    int local_start = rank * local_height;
    int local_end = local_start + local_height;

    size_t pitch_bytes;
    unsigned char* d_current;
    unsigned char* d_next;

    // Выделение памяти с поддержкой нескольких GPU
    cudaSetDevice(rank % world_size); // Предполагаем, что GPU = world_size

    cudaMallocPitch(&d_current, &pitch_bytes, width * sizeof(unsigned char), local_height);
    cudaMallocPitch(&d_next, &pitch_bytes, width * sizeof(unsigned char), local_height);

    // Инициализация локальной части сетки
    unsigned char* h_current = new unsigned char[local_height * width];
    memset(h_current, 0, local_height * width * sizeof(unsigned char));

    // Только корень инициализирует glider
    if (rank == 0) {
        initialize_glider(h_current, width, local_height, width);
    }

    cudaMemcpy2D(d_current, pitch_bytes, h_current, width * sizeof(unsigned char),
                 width * sizeof(unsigned char), local_height, cudaMemcpyHostToDevice);

    dim3 block(BLOCK_SIZE, BLOCK_SIZE);
    dim3 grid((width + block.x - 1) / block.x, (local_height + block.y - 1) / block.y);

    for (int step = 0; step < steps; step++) {
        game_of_life_kernel<<<grid, block>>>(
            d_current, d_next, width, local_height, pitch_bytes
        );
        cudaDeviceSynchronize();

        // Обмен границами с соседями (для периодических условий)
        if (rank > 0) {
            // Отправляем верхнюю границу вниз процессу rank-1
            unsigned char* top_row = (unsigned char*)((char*)d_current);
            unsigned char* recv_buf;
            cudaMalloc(&recv_buf, width * sizeof(unsigned char));
            MPI_Send(top_row, width, MPI_UBYTE, rank - 1, 0, MPI_COMM_WORLD);
            MPI_Recv(recv_buf, width, MPI_UBYTE, rank - 1, 1, MPI_COMM_WORLD, MPI_STATUS_IGNORE);
            cudaMemcpy((unsigned char*)((char*)d_current + (local_height - 1) * pitch_bytes), recv_buf, width, cudaMemcpyHostToDevice);
            cudaFree(recv_buf);
        }
        if (rank < world_size - 1) {
            // Отправляем нижнюю границу вверх процессу rank+1
            unsigned char* bottom_row = (unsigned char*)((char*)d_current + (local_height - 1) * pitch_bytes);
            unsigned char* recv_buf;
            cudaMalloc(&recv_buf, width * sizeof(unsigned char));
            MPI_Send(bottom_row, width, MPI_UBYTE, rank + 1, 1, MPI_COMM_WORLD);
            MPI_Recv(recv_buf, width, MPI_UBYTE, rank + 1, 0, MPI_COMM_WORLD, MPI_STATUS_IGNORE);
            cudaMemcpy((unsigned char*)((char*)d_current), recv_buf, width, cudaMemcpyHostToDevice);
            cudaFree(recv_buf);
        }

        // Переключение буферов
        std::swap(d_current, d_next);
    }

    // Копируем результат обратно
    unsigned char* h_result = new unsigned char[local_height * width];
    cudaMemcpy2D(h_result, width * sizeof(unsigned char), d_current, pitch_bytes,
                 width * sizeof(unsigned char), local_height, cudaMemcpyDeviceToHost);

    // Сборка полного изображения (на процессе 0)
    if (rank == 0) {
        std::vector<unsigned char> full_grid(height * width);
        MPI_Gather(h_result, local_height * width, MPI_UBYTE,
                   full_grid.data(), local_height * width, MPI_UBYTE, 0, MPI_COMM_WORLD);

        // Сохранение финального состояния
        save_grid_to_ppm(full_grid.data(), width, height, width, "final_state.ppm");
    } else {
        MPI_Gather(h_result, local_height * width, MPI_UBYTE,
                   nullptr, 0, MPI_UBYTE, 0, MPI_COMM_WORLD);
    }

    // Очистка
    cudaFree(d_current);
    cudaFree(d_next);
    delete[] h_current;
    delete[] h_result;
}
