#ifndef KNN_WRAPPER_HPP
#define KNN_WRAPPER_HPP

#include <vector>
#include <string>
#include <memory>
#include <cuda_runtime.h>

struct KNNQueryResult {
    std::vector<int> indices;
    std::vector<float> distances;
};

class KNNWrapper {
public:
    // Конструктор
    KNNWrapper(int dim, int k, bool debug = false);

    // Деструктор
    ~KNNWrapper();

    // Загрузка данных из файла (формат: float, построчно)
    bool load_data(const std::string& filename);

    // Построение графа KNN (на GPU)
    bool build_graph();

    // Уточнение графа (например, mutual KNN)
    bool refine_graph();

    // Генерация эталонных данных (ground truth) — полный перебор
    bool generate_ground_truth();

    // Оценка графа: precision@k, recall@k
    void evaluate_graph(double& precision, double& recall) const;

    // Выполнение запроса KNN для одного вектора
    KNNQueryResult query(const std::vector<float>& query_vec) const;

    // Выполнение запроса KNN для нескольких векторов
    std::vector<KNNQueryResult> batch_query(const std::vector<std::vector<float>>& queries) const;

    // Сохранение графа в файл
    bool save_graph(const std::string& filename) const;

    // Загрузка графа из файла
    bool load_graph(const std::string& filename);

    // Сброс графа для подмножества индексов
    bool reset_graph_for_subset(const std::vector<int>& subset_indices);

    // Получить размер данных
    int get_num_points() const { return num_points_; }
    int get_dim() const { return dim_; }
    int get_k() const { return k_; }

    // Включить/выключить отладочный вывод
    void set_debug_mode(bool debug) { debug_ = debug; }

private:
    int dim_;           // размерность векторов
    int k_;             // число соседей
    int num_points_;    // количество точек
    bool debug_;

    // Хост-данные
    std::vector<float> h_data_;           // [num_points * dim]
    std::vector<int>   h_graph_indices_;  // [num_points * k]
    std::vector<float> h_graph_distances_; // [num_points * k]

    // Устройство-указатели
    float* d_data_ = nullptr;
    int*   d_graph_indices_ = nullptr;
    float* d_graph_distances_ = nullptr;

    // Эталонные данные (ground truth)
    std::vector<int>   h_gt_indices_;
    std::vector<float> h_gt_distances_;

    // CUDA-устройства
    cudaStream_t stream_;

    // Вспомогательные функции
    void allocate_device_memory();
    void free_device_memory();
    void copy_data_to_device();
    void copy_data_from_device();
    void compute_l2_distance(float* d_dist, const float* d_a, const float* d_b, int n, cudaStream_t stream);
    void find_knn_cuda();
    void mutual_knn_refinement();
    void compute_ground_truth_cpu();
};

#endif // KNN_WRAPPER_HPP
