## ✅ 2. `knn_wrapper.cu` — Реализация (CUDA)

```cpp
#include "knn_wrapper.hpp"
#include <iostream>
#include <fstream>
#include <sstream>
#include <algorithm>
#include <cmath>
#include <cub/cub.cuh>

// Ядро: вычисление L2 расстояния между двумя векторами
__global__ void compute_l2_distance_kernel(const float* a, const float* b, float* dist, int dim) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= dim) return;
    float diff = a[idx] - b[idx];
    atomicAdd(dist, diff * diff);
}

// Ядро: поиск k ближайших соседей (упрощённая версия)
__global__ void find_knn_kernel(
    const float* data,
    int* indices,
    float* distances,
    int num_points,
    int dim,
    int k,
    cudaStream_t stream
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    // Временные массивы для хранения соседей
    float* dists = (float*)malloc(k * sizeof(float));
    int* idxs = (int*)malloc(k * sizeof(int));

    for (int i = 0; i < k; ++i) {
        dists[i] = INFINITY;
        idxs[i] = -1;
    }

    const float* query = &data[idx * dim];

    for (int j = 0; j < num_points; ++j) {
        if (j == idx) continue;

        float dist = 0.0f;
        for (int d = 0; d < dim; ++d) {
            float diff = query[d] - data[j * dim + d];
            dist += diff * diff;
        }

        // Вставка в топ-k
        for (int i = 0; i < k; ++i) {
            if (dist < dists[i]) {
                for (int l = k - 1; l > i; --l) {
                    dists[l] = dists[l - 1];
                    idxs[l] = idxs[l - 1];
                }
                dists[i] = dist;
                idxs[i] = j;
                break;
            }
        }
    }

    // Копирование результата
    for (int i = 0; i < k; ++i) {
        indices[idx * k + i] = idxs[i];
        distances[idx * k + i] = dists[i];
    }

    free(idxs);
    free(dists);
}

// Ядро: mutual KNN refinement
__global__ void mutual_knn_refinement_kernel(
    int* graph_indices,
    int num_points,
    int k
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= num_points) return;

    for (int i = 0; i < k; ++i) {
        int neighbor = graph_indices[idx * k + i];
        if (neighbor == -1) continue;

        bool mutual = false;
        for (int j = 0; j < k; ++j) {
            if (graph_indices[neighbor * k + j] == idx) {
                mutual = true;
                break;
            }
        }
        if (!mutual) {
            graph_indices[idx * k + i] = -1; // помечаем как не-mutual
        }
    }
}

// Реализация методов
KNNWrapper::KNNWrapper(int dim, int k, bool debug)
    : dim_(dim), k_(k), num_points_(0), debug_(debug), stream_(0) {
    cudaStreamCreate(&stream_);
}

KNNWrapper::~KNNWrapper() {
    free_device_memory();
    cudaStreamDestroy(stream_);
}

bool KNNWrapper::load_data(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Ошибка: не удалось открыть файл " << filename << std::endl;
        return false;
    }

    std::string line;
    std::vector<float> temp_data;
    while (std::getline(file, line)) {
        std::stringstream ss(line);
        float val;
        while (ss >> val) {
            temp_data.push_back(val);
        }
    }
    file.close();

    if (temp_data.size() % dim_ != 0) {
        std::cerr << "Ошибка: размер данных не кратен размерности" << std::endl;
        return false;
    }

    num_points_ = temp_data.size() / dim_;
    h_data_ = std::move(temp_data);
    return true;
}

void KNNWrapper::allocate_device_memory() {
    cudaMalloc(&d_data_, num_points_ * dim_ * sizeof(float));
    cudaMalloc(&d_graph_indices_, num_points_ * k_ * sizeof(int));
    cudaMalloc(&d_graph_distances_, num_points_ * k_ * sizeof(float));
}

void KNNWrapper::free_device_memory() {
    if (d_data_) cudaFree(d_data_);
    if (d_graph_indices_) cudaFree(d_graph_indices_);
    if (d_graph_distances_) cudaFree(d_graph_distances_);
    d_data_ = nullptr;
    d_graph_indices_ = nullptr;
    d_graph_distances_ = nullptr;
}

void KNNWrapper::copy_data_to_device() {
    cudaMemcpyAsync(d_data_, h_data_.data(), num_points_ * dim_ * sizeof(float),
                    cudaMemcpyHostToDevice, stream_);
}

void KNNWrapper::copy_data_from_device() {
    h_graph_indices_.resize(num_points_ * k_);
    h_graph_distances_.resize(num_points_ * k_);
    cudaMemcpyAsync(h_graph_indices_.data(), d_graph_indices_,
                    num_points_ * k_ * sizeof(int), cudaMemcpyDeviceToHost, stream_);
    cudaMemcpyAsync(h_graph_distances_.data(), d_graph_distances_,
                    num_points_ * k_ * sizeof(float), cudaMemcpyDeviceToHost, stream_);
    cudaStreamSynchronize(stream_);
}

void KNNWrapper::compute_l2_distance(float* d_dist, const float* d_a, const float* d_b, int n, cudaStream_t stream) {
    float* temp_dist;
    cudaMalloc(&temp_dist, sizeof(float));
    cudaMemsetAsync(temp_dist, 0, sizeof(float), stream);

    int blockSize = 256;
    int gridSize = (n + blockSize - 1) / blockSize;
    compute_l2_distance_kernel<<<gridSize, blockSize, 0, stream>>>(d_a, d_b, temp_dist, n);
    cudaMemcpyAsync(d_dist, temp_dist, sizeof(float), cudaMemcpyDeviceToDevice, stream);
    cudaFree(temp_dist);
}

void KNNWrapper::find_knn_cuda() {
    int blockSize = 256;
    int gridSize = (num_points_ + blockSize - 1) / blockSize;
    find_knn_kernel<<<gridSize, blockSize, 0, stream_>>>(
        d_data_, d_graph_indices_, d_graph_distances_,
        num_points_, dim_, k_, stream_
    );
    cudaStreamSynchronize(stream_);
}

bool KNNWrapper::build_graph() {
    if (num_points_ == 0) {
        std::cerr << "Ошибка: данные не загружены" << std::endl;
        return false;
    }

    allocate_device_memory();
    copy_data_to_device();

    find_knn_cuda();

    copy_data_from_device();

    if (debug_) {
        std::cout << "Граф KNN построен: " << num_points_ << " точек, k=" << k_ << std::endl;
    }

    return true;
}

bool KNNWrapper::refine_graph() {
    if (h_graph_indices_.empty()) {
        std::cerr << "Ошибка: граф не построен" << std::endl;
        return false;
    }

    // Копируем на GPU
    cudaMemcpy(d_graph_indices_, h_graph_indices_.data(),
               num_points_ * k_ * sizeof(int), cudaMemcpyHostToDevice);

    int blockSize = 256;
    int gridSize = (num_points_ + blockSize - 1) / blockSize;
    mutual_knn_refinement_kernel<<<gridSize, blockSize, 0, stream_>>>(
        d_graph_indices_, num_points_, k_
    );
    cudaStreamSynchronize(stream_);

    // Копируем обратно
    cudaMemcpy(h_graph_indices_.data(), d_graph_indices_,
               num_points_ * k_ * sizeof(int), cudaMemcpyDeviceToHost);

    if (debug_) {
        std::cout << "Граф уточнён (mutual KNN)" << std::endl;
    }

    return true;
}

void KNNWrapper::compute_ground_truth_cpu() {
    h_gt_indices_.resize(num_points_ * k_);
    h_gt_distances_.resize(num_points_ * k_);

    for (int i = 0; i < num_points_; ++i) {
        std::vector<std::pair<float, int>> dists;
        for (int j = 0; j < num_points_; ++j) {
            if (i == j) continue;
            float dist = 0.0f;
            for (int d = 0; d < dim_; ++d) {
                float diff = h_data_[i * dim_ + d] - h_data_[j * dim_ + d];
                dist += diff * diff;
            }
            dists.emplace_back(dist, j);
        }
        std::sort(dists.begin(), dists.end());
        for (int k = 0; k < this->k_; ++k) {
            if (k < dists.size()) {
                h_gt_indices_[i * this->k_ + k] = dists[k].second;
                h_gt_distances_[i * this->k_ + k] = dists[k].first;
            } else {
                h_gt_indices_[i * this->k_ + k] = -1;
                h_gt_distances_[i * this->k_ + k] = INFINITY;
            }
        }
    }
}

bool KNNWrapper::generate_ground_truth() {
    if (num_points_ == 0) {
        std::cerr << "Ошибка: данные не загружены" << std::endl;
        return false;
    }
    compute_ground_truth_cpu();
    if (debug_) {
        std::cout << "Эталонные данные сгенерированы" << std::endl;
    }
    return true;
}

void KNNWrapper::evaluate_graph(double& precision, double& recall) const {
    if (h_gt_indices_.empty()) {
        std::cerr << "Ошибка: эталонные данные не сгенерированы" << std::endl;
        precision = recall = 0.0;
        return;
    }

    int correct = 0;
    int total = 0;

    for (int i = 0; i < num_points_; ++i) {
        for (int k = 0; k < k_; ++k) {
            int pred_idx = h_graph_indices_[i * k_ + k];
            int gt_idx = h_gt_indices_[i * k_ + k];
            if (pred_idx == gt_idx && pred_idx != -1) {
                correct++;
            }
            if (gt_idx != -1) {
                total++;
            }
        }
    }

    precision = (total > 0) ? static_cast<double>(correct) / (num_points_ * k_) : 0.0;
    recall = (total > 0) ? static_cast<double>(correct) / total : 0.0;
}

KNNQueryResult KNNWrapper::query(const std::vector<float>& query_vec) const {
    if (query_vec.size() != dim_) {
        throw std::invalid_argument("Размер запроса не совпадает с размерностью");
    }

    KNNQueryResult result;
    result.indices.resize(k_);
    result.distances.resize(k_);

    // На CPU для простоты (можно перенести на GPU)
    std::vector<std::pair<float, int>> dists;
    for (int j = 0; j < num_points_; ++j) {
        float dist = 0.0f;
        for (int d = 0; d < dim_; ++d) {
            float diff = query_vec[d] - h_data_[j * dim_ + d];
            dist += diff * diff;
        }
        dists.emplace_back(dist, j);
    }
    std::sort(dists.begin(), dists.end());
    for (int k = 0; k < this->k_; ++k) {
        if (k < dists.size()) {
            result.indices[k] = dists[k].second;
            result.distances[k] = dists[k].first;
        } else {
            result.indices[k] = -1;
            result.distances[k] = INFINITY;
        }
    }

    return result;
}

std::vector<KNNQueryResult> KNNWrapper::batch_query(const std::vector<std::vector<float>>& queries) const {
    std::vector<KNNQueryResult> results;
    for (const auto& q : queries) {
        results.push_back(query(q));
    }
    return results;
}

bool KNNWrapper::save_graph(const std::string& filename) const {
    std::ofstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Ошибка: не удалось открыть файл для записи" << std::endl;
        return false;
    }

    file << dim_ << " " << k_ << " " << num_points_ << "\n";
    for (int i = 0; i < num_points_; ++i) {
        for (int j = 0; j < k_; ++j) {
            file << h_graph_indices_[i * k_ + j] << " ";
        }
        file << "\n";
    }
    file.close();
    return true;
}

bool KNNWrapper::load_graph(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Ошибка: не удалось открыть файл для чтения" << std::endl;
        return false;
    }

    int dim, k, n;
    file >> dim >> k >> n;
    if (dim != dim_ || k != k_) {
        std::cerr << "Ошибка: несоответствие параметров при загрузке графа" << std::endl;
        file.close();
        return false;
    }

    num_points_ = n;
    h_graph_indices_.resize(n * k);
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            file >> h_graph_indices_[i * k + j];
        }
    }
    file.close();

    // Инициализируем расстояния (можно улучшить)
    h_graph_distances_.assign(n * k, 0.0f);

    if (debug_) {
        std::cout << "Граф загружен из файла" << std::endl;
    }

    return true;
}

bool KNNWrapper::reset_graph_for_subset(const std::vector<int>& subset_indices) {
    if (subset_indices.empty()) return true;

    // Инициализируем граф для подмножества
    for (int idx : subset_indices) {
        if (idx >= 0 && idx < num_points_) {
            for (int k = 0; k < k_; ++k) {
                h_graph_indices_[idx * k_ + k] = -1;
                h_graph_distances_[idx * k_ + k] = INFINITY;
            }
        }
    }

    if (debug_) {
        std::cout << "Граф сброшен для " << subset_indices.size() << " точек" << std::endl;
    }

    return true;
}
