## ✅ 3. `main.cpp` — Пример использования

```cpp
#include "knn_wrapper.hpp"
#include <iostream>

int main() {
    KNNWrapper knn(3, 5, true); // dim=3, k=5, debug=true

    if (!knn.load_data("data.txt")) {
        std::cerr << "Не удалось загрузить данные" << std::endl;
        return -1;
    }

    if (!knn.build_graph()) {
        std::cerr << "Не удалось построить граф" << std::endl;
        return -1;
    }

    if (!knn.refine_graph()) {
        std::cerr << "Не удалось уточнить граф" << std::endl;
    }

    if (!knn.generate_ground_truth()) {
        std::cerr << "Не удалось сгенерировать эталон" << std::endl;
    }

    double precision, recall;
    knn.evaluate_graph(precision, recall);
    std::cout << "Precision: " << precision << ", Recall: " << recall << std::endl;

    // Запрос
    std::vector<float> query = {1.0f, 2.0f, 3.0f};
    auto result = knn.query(query);
    std::cout << "Query result: ";
    for (int i = 0; i < result.indices.size(); ++i) {
        std::cout << result.indices[i] << "(" << result.distances[i] << ") ";
    }
    std::cout << std::endl;

    // Сохранение и загрузка
    knn.save_graph("graph.bin");
    KNNWrapper knn2(3, 5, false);
    knn2.load_graph("graph.bin");

    // Сброс для подмножества
    std::vector<int> subset = {0, 1, 2};
    knn.reset_graph_for_subset(subset);

    return 0;
}
