Вот реализация метода `build` для структуры `TransportBuilder`, который создаёт HTTP-транспорт для работы с Elasticsearch. Мы будем использовать стандартную библиотеку Go (`net/http`) и предположим, что `Transport` — это тип, оборачивающий `*http.Client`, или интерфейс, принимающий `http.Client`. Ниже приведён пример на чистом Go с использованием стандартных средств.

```go
package main

import (
	"crypto/tls"
	"crypto/x509"
	"net"
	"net/http"
	"net/url"
	"time"

	"golang.org/x/net/proxy"
)

// Transport представляет HTTP-клиент для отправки запросов в Elasticsearch.
type Transport struct {
	*http.Client
}

// TransportBuilder используется для построения Transport с нужными настройками.
type TransportBuilder struct {
	proxyURL     *url.URL
	proxyUser    string
	proxyPass    string
	disableProxy bool
	username     string
	password     string
	certPool     *x509.CertPool
	headers      map[string]string
	timeout      time.Duration
}

// NewTransportBuilder создаёт новый экземпляр TransportBuilder с настройками по умолчанию.
func NewTransportBuilder() *TransportBuilder {
	return &TransportBuilder{
		headers: make(map[string]string),
		timeout: 30 * time.Second, // таймаут по умолчанию
	}
}

// WithProxy настраивает прокси с необязательной базовой аутентификацией.
func (b *TransportBuilder) WithProxy(proxyURL string, username, password string) *TransportBuilder {
	url, err := url.Parse(proxyURL)
	if err != nil {
		// Можно логировать ошибку, но в данном случае просто игнорируем
		return b
	}
	b.proxyURL = url
	b.proxyUser = username
	b.proxyPass = password
	b.disableProxy = false
	return b
}

// WithoutProxy отключает использование прокси, включая системные.
func (b *TransportBuilder) WithoutProxy() *TransportBuilder {
	b.disableProxy = true
	return b
}

// WithCredentials устанавливает учетные данные для аутентификации в Elasticsearch.
func (b *TransportBuilder) WithCredentials(username, password string) *TransportBuilder {
	b.username = username
	b.password = password
	return b
}

// WithCertificatePool применяет проверку сертификата через предоставленный CertPool.
func (b *TransportBuilder) WithCertificatePool(certPool *x509.CertPool) *TransportBuilder {
	b.certPool = certPool
	return b
}

// WithHeader добавляет HTTP-заголовок, который будет применяться ко всем запросам.
func (b *TransportBuilder) WithHeader(key, value string) *TransportBuilder {
	b.headers[key] = value
	return b
}

// WithTimeout устанавливает глобальный тайм-аут для клиента.
func (b *TransportBuilder) WithTimeout(timeout time.Duration) *TransportBuilder {
	b.timeout = timeout
	return b
}

// build создаёт и возвращает Transport на основе настроек.
func (b *TransportBuilder) build() (*Transport, error) {
	// Создаём базовый транспорт
	transport := &http.Transport{
		MaxIdleConns:          100,
		IdleConnTimeout:       90 * time.Second,
		TLSHandshakeTimeout:   10 * time.Second,
		ResponseHeaderTimeout: 10 * time.Second,
		ExpectContinueTimeout: 1 * time.Second,
	}

	// Настройка TLS
	if b.certPool != nil {
		transport.TLSClientConfig = &tls.Config{
			RootCAs: b.certPool,
		}
	}

	// Настройка прокси
	if !b.disableProxy {
		if b.proxyURL != nil {
			// Прокси с аутентификацией
			proxyDialer, err := proxy.FromURL(b.proxyURL, proxy.Direct)
			if err != nil {
				return nil, err
			}

			transport.Proxy = func(req *http.Request) (*url.URL, error) {
				return b.proxyURL, nil
			}

			// Обёртка для добавления Basic Auth к прокси
			transport.DialContext = func(ctx context.Context, network, addr string) (net.Conn, error) {
				conn, err := proxyDialer.DialContext(ctx, network, addr)
				if err != nil {
					return nil, err
				}

				// Если есть логин/пароль — добавляем Basic Auth
				if b.proxyUser != "" && b.proxyPass != "" {
					auth := "Basic " + b64.EncodeToString([]byte(b.proxyUser+":"+b.proxyPass))
					conn.Write([]byte("CONNECT " + addr + " HTTP/1.1\r\n"))
					conn.Write([]byte("Host: " + addr + "\r\n"))
					conn.Write([]byte("Proxy-Authorization: " + auth + "\r\n"))
					conn.Write([]byte("\r\n"))

					// Читаем ответ
					var buf [1024]byte
					n, _ := conn.Read(buf[:])
					response := string(buf[:n])
					if !strings.Contains(response, "200") {
						conn.Close()
						return nil, fmt.Errorf("proxy authentication failed: %s", response)
					}
				}
				return conn, nil
			}
		} else {
			// Использовать системные прокси (если не отключены)
			transport.Proxy = http.ProxyFromEnvironment
		}
	} else {
		// Отключаем прокси полностью
		transport.Proxy = nil
	}

