#### 3. `build.js` — основной скрипт сборки

```javascript
const fs = require('fs');
const path = require('path');
const Handlebars = require('handlebars');
const { ParserGenerator } = require('chevrotain'); // Пример: генерация парсера

// Пути
const SRC_DIR = path.join(__dirname, 'src');
const BUILD_DIR = path.join(__dirname, 'build', 'generated');
const PUBLIC_DIR = path.join(__dirname, 'public');

// Создание директорий
function ensureDir(dir) {
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true });
  }
}

// Генерация парсера (пример с Chevrotain)
function generateParser() {
  console.log('Генерация парсера...');
  const grammarPath = path.join(SRC_DIR, 'parsers', 'grammar.js');
  const parserOutput = path.join(BUILD_DIR, 'Parser.js');

  // Здесь можно использовать Chevrotain API для генерации парсера
  // Упрощённый пример: копируем или генерируем
  const parserCode = `
    // Сгенерированный парсер
    class GeneratedParser {
      parse(input) {
        return { success: true, result: input.toUpperCase() };
      }
    }
    module.exports = GeneratedParser;
  `;

  fs.writeFileSync(parserOutput, parserCode);
  console.log(`Парсер сохранён: ${parserOutput}`);
}

// Генерация шаблонов
function generateTemplates() {
  console.log('Генерация шаблонов...');
  const templatePath = path.join(SRC_DIR, 'templates', 'page.hbs');
  const dataPath = path.join(SRC_DIR, 'data', 'config.json');
  const outputPath = path.join(PUBLIC_DIR, 'index.html');

  const templateSource = fs.readFileSync(templatePath, 'utf8');
  const data = JSON.parse(fs.readFileSync(dataPath, 'utf8'));
  const template = Handlebars.compile(templateSource);
  const result = template(data);

  fs.writeFileSync(outputPath, result);
  console.log(`Шаблон сгенерирован: ${outputPath}`);
}

// Копирование статических ресурсов
function copyAssets() {
  console.log('Копирование статических файлов...');
  const assetsDir = path.join(SRC_DIR, 'assets');
  if (fs.existsSync(assetsDir)) {
    const files = fs.readdirSync(assetsDir);
    files.forEach(file => {
      const src = path.join(assetsDir, file);
      const dest = path.join(PUBLIC_DIR, file);
      fs.copyFileSync(src, dest);
      console.log(`Скопировано: ${file}`);
    });
  }
}

// Очистка
function clean() {
  console.log('Очистка...');
  if (fs.existsSync(PUBLIC_DIR)) {
    fs.rmSync(PUBLIC_DIR, { recursive: true, force: true });
  }
  if (fs.existsSync(BUILD_DIR)) {
    fs.rmSync(BUILD_DIR, { recursive: true, force: true });
  }
  ensureDir(PUBLIC_DIR);
  ensureDir(BUILD_DIR);
}

// Основная логика
function main() {
  const args = process.argv.slice(2);

  if (args.includes('clean')) {
    clean();
    return;
  }

  ensureDir(PUBLIC_DIR);
  ensureDir(BUILD_DIR);

  if (args.includes('generate-parser')) {
    generateParser();
  }
  if (args.includes('generate-templates')) {
    generateTemplates();
  }
  if (args.includes('copy-assets')) {
    copyAssets();
  }

  // Если не указаны команды — делаем полную сборку
  if (args.length === 0) {
    generateParser();
    generateTemplates();
    copyAssets();
    console.log('✅ Сборка завершена!');
  }
}

main();
```

