## ✅ 3. Обертка для MAPS-Multi: `maps_multi_wrapper.cu`

```cpp
// maps_multi_wrapper.cu
#include "gol_kernel.cu"
#include "utils.cu"
#include <vector>
#include <string>
#include <iostream>

// Структура для хранения данных на GPU
struct GOLState {
    bool* d_grid_in;
    bool* d_grid_out;
    int width, height;
    size_t size;
};

// Инициализация состояния на GPU
void init_gol_state(GOLState& state, int width, int height, int device_id) {
    state.width = width;
    state.height = height;
    state.size = width * height;

    cudaSetDevice(device_id);
    cudaMalloc(&state.d_grid_in, state.size * sizeof(bool));
    cudaMalloc(&state.d_grid_out, state.size * sizeof(bool));
}

// Освобождение памяти
void free_gol_state(GOLState& state, int device_id) {
    cudaSetDevice(device_id);
    cudaFree(state.d_grid_in);
    cudaFree(state.d_grid_out);
}

// Запуск одного шага на GPU
void run_gol_step(GOLState& state, int device_id) {
    dim3 blockSize(BLOCK_SIZE, BLOCK_SIZE);
    dim3 gridSize(
        (state.width + blockSize.x - 1) / blockSize.x,
        (state.height + blockSize.y - 1) / blockSize.y
    );

    cudaSetDevice(device_id);
    gol_step_kernel<<<gridSize, blockSize>>>(
        state.d_grid_in,
        state.d_grid_out,
        state.width,
        state.height
    );
    cudaDeviceSynchronize();
}

// Обмен буферами (in <-> out)
void swap_grids(GOLState& state, int device_id) {
    cudaSetDevice(device_id);
    bool* temp = state.d_grid_in;
    state.d_grid_in = state.d_grid_out;
    state.d_grid_out = temp;
}

// Регрессионный тест: сравнение с эталонным результатом
bool run_regression_test(
    GOLState& state,
    const bool* expected,
    int device_id,
    const std::string& test_name
) {
    bool* h_result = new bool[state.size];
    copy_grid_from_device(state.d_grid_in, h_result, state.size, device_id);

    bool passed = compare_grids(h_result, expected, state.size);

    if (!passed) {
        std::string filename = "regression_fail_" + test_name + "_gpu" + std::to_string(device_id) + ".ppm";
        save_grid_as_ppm(filename, h_result, state.width, state.height);
        fprintf(stderr, "Regression test failed: %s on GPU %d. Saved to %s\n",
                test_name.c_str(), device_id, filename.c_str());
    }

    delete[] h_result;
    return passed;
}
```

