## ✅ 4. Основной файл: `main.cpp`

```cpp
// main.cpp
#include "maps_multi_wrapper.cu"
#include <vector>
#include <iostream>
#include <cassert>

// Пример начальной конфигурации: "blinker"
void init_blinker(bool* grid, int width, int height) {
    assert(width >= 3 && height >= 3);
    for (int i = 0; i < width * height; ++i) grid[i] = false;
    grid[1 * width + 1] = true;
    grid[1 * width + 2] = true;
    grid[1 * width + 3] = true;
}

// Эталонный результат после одного шага (blinker → горизонтальный)
void expected_blinker_step1(bool* expected, int width, int height) {
    for (int i = 0; i < width * height; ++i) expected[i] = false;
    expected[2 * width + 1] = true;
    expected[2 * width + 2] = true;
    expected[2 * width + 3] = true;
}

int main() {
    int num_gpus;
    cudaGetDeviceCount(&num_gpus);
    if (num_gpus == 0) {
        fprintf(stderr, "No CUDA devices found.\n");
        return -1;
    }

    const int width = 100;
    const int height = 100;
    const int size = width * height;

    std::vector<GOLState> states(num_gpus);
    std::vector<bool*> h_grids(num_gpus, nullptr);
    std::vector<bool*> expected_results(num_gpus, nullptr);

    // Инициализация на каждом GPU
    for (int i = 0; i < num_gpus; ++i) {
        init_gol_state(states[i], width, height, i);
        h_grids[i] = new bool[size];
        expected_results[i] = new bool[size];

        // Копируем начальную конфигурацию на хост
        init_blinker(h_grids[i], width, height);
        expected_blinker_step1(expected_results[i], width, height);

        // Копируем на GPU
        cudaSetDevice(i);
        cudaMemcpy(states[i].d_grid_in, h_grids[i], size * sizeof(bool), cudaMemcpyHostToDevice);
    }

    // Выполнение одного шага на всех GPU
    for (int i = 0; i < num_gpus; ++i) {
        run_gol_step(states[i], i);
        swap_grids(states[i], i); // d_grid_in теперь содержит результат
    }

    // Регрессионные тесты
    bool all_passed = true;
    for (int i = 0; i < num_gpus; ++i) {
        if (!run_regression_test(states[i], expected_results[i], i, "blinker_step1")) {
            all_passed = false;
        }
    }

    if (all_passed) {
        printf("All regression tests passed on %d GPUs.\n", num_gpus);
    } else {
        printf("Some regression tests failed.\n");
    }

    // Очистка
    for (int i = 0; i < num_gpus; ++i) {
        free_gol_state(states[i], i);
        delete[] h_grids[i];
        delete[] expected_results[i];
    }

    return all_passed ? 0 : 1;
}
```

