    int max_degree_;      // Максимальная степень вершины
    int ef_construction_; // Размер эвристики при построении

    // Данные (Хост)
    int n_points_;
    int n_dims_;
    std::vector<float> data_;       // Точки данных
    std::vector<int> graph_;       // Граф (списки смежности)
    std::vector<int> labels_;      // Эталонные метки (для оценки)

    // Данные (Устройство)
    float* d_data_;
    int* d_graph_;
    int* d_labels_;

    // Параметры для ядра поиска
    int max_ef_; // Максимальный размер очереди кандидатов при поиске

    bool debug_mode;

    // Внутренние методы
    void InitializeGPU();
    void CleanupGPU();
    float BruteForceSearch(const std::vector<float>& query, int k, std::vector<int>& result);
