#### 2. KNNWrapper.cu

```cpp
#include "KNNWrapper.h"
#include <queue>
#include <cstdio>

// --------------------------------------------------------
// CUDA Ядра (Kernels)
// --------------------------------------------------------

// Функция расстояния L2 (Евклидово)
__device__ float L2Distance(const float* a, const float* b, int dim) {
    float sum = 0.0f;
    for (int i = 0; i < dim; ++i) {
        float diff = a[i] - b[i];
        sum += diff * diff;
    }
    return sqrtf(sum);
}

// Ядро: Построение графа (упрощенная версия: соединяем каждую точку с K ближайшими)
__global__ void BuildKNNGraphKernel(
    const float* d_data, 
    int* d_graph, 
    int n_points, 
    int n_dims, 
    int k, 
    int max_degree
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n_points) return;

    // Вычисляем расстояния до всех других точек
    // В реальном HNSW используется иерархия, здесь для примера - линейный скан

    // Буфер для хранения пар (distance, index)
    // Используем ограниченный размер max_degree для хранения лучших кандидатов
    float dists[max_degree + 1]; 
    int indices[max_degree + 1];

    for (int i = 0; i < max_degree + 1; ++i) {
        dists[i] = 1e10f;
        indices[i] = -1;
    }

    const float* current_point = &d_data[idx * n_dims];

    for (int i = 0; i < n_points; ++i) {
        if (i == idx) continue;

        const float* other_point = &d_data[i * n_dims];
        float dist = L2Distance(current_point, other_point, n_dims);

        // Обновляем топ-K соседей
        // Если нашли ближе, сдвигаем массив
        for (int j = 0; j < max_degree; ++j) {
            if (dist < dists[j]) {
                // Сдвиг
                for (int k = max_degree - 1; k > j; --k) {
                    dists[k] = dists[k-1];
                    indices[k] = indices[k-1];
                }
                dists[j] = dist;
                indices[j] = i;
                break;
            }
        }
    }

    // Записываем результат в граф (адресация плоским массивом)
    // Допустим, каждый узел занимает 16 байт (max_degree * 4)
    int base_offset = idx * (max_degree * sizeof(int));
    int* ptr = (int*)((char*)d_graph + base_offset);

    for (int i = 0; i < max_degree; ++i) {
        ptr[i] = indices[i];
    }
}

// Ядро: Поиск KNN на графе
// Используем очередь с приоритетом (Priority Queue) на GPU
__global__ void SearchKNNGraphKernel(
    const float* d_data,
    const int* d_graph,
    const float* d_query,
    int* d_result_indices,
    float* d_result_dists,
    int n_points,
    int n_dims,
    int k,
    int max_degree,
    int max_ef
) {
    int idx = blockIdx.x * blockDim.x + threadIdx.x;
    if (idx >= n_points) return;

    // Простая реализация очереди на GPU:
    // Используем глобальный массив для пар (dist, index)
    // Индекс потока определяет позицию в очереди (упрощенно)

    // В реальной реализации здесь используется сложная структура данных (например, heap)
    // Для простоты примера мы возвращаем топ-k соседей, найденных при инициализации

    // В этом примере мы просто копируем результаты из памяти графа
    // В полноценном алгоритме здесь должна быть логика обхода графа (BFS/DFS) с приоритетами

    int base_offset = idx * (max_degree * sizeof(int));
    const int* neighbors = (const int*)((const char*)d_graph + base_offset);

    for (int i = 0; i < k; ++i) {
        d_result_indices[idx * k + i] = neighbors[i];
        d_result_dists[idx * k + i] = 0.0f; // В реальном коде нужно вычислять дистанцию
    }
}

// --------------------------------------------------------
// Реализация класса (Host side)
// --------------------------------------------------------

KNNGraphWrapper::KNNGraphWrapper(int k, int max_degree, int ef_construction)
    : k_(k), max_degree_(max_degree), ef_construction_(ef_construction), debug_mode(false) {

    max_ef_ = ef_construction;
    n_points_ = 0;
    n_dims_ = 0;
    d_data_ = nullptr;
    d_graph_ = nullptr;
    d_labels_ = nullptr;
}

KNNGraphWrapper::~KNNGraphWrapper() {
    CleanupGPU();
}

void KNNGraphWrapper::InitializeGPU() {
    if (d_data_ == nullptr && n_points_ > 0) {
        size_t data_size = n_points_ * n_dims_ * sizeof(float);
        size_t graph_size = n_points_ * max_degree_ * sizeof(int);
        size_t label_size = n_points_ * sizeof(int);

        CUDA_CHECK(cudaMalloc(&d_data_, data_size));
        CUDA_CHECK(cudaMalloc(&d_graph_, graph_size));
        CUDA_CHECK(cudaMalloc(&d_labels_, label_size));

        CUDA_CHECK(cudaMemcpy(d_data_, data_.data(), data_size, cudaMemcpyHostToDevice));
        CUDA_CHECK(cudaMemcpy(d_labels_, labels_.data(), label_size, cudaMemcpyHostToDevice));

        // Инициализируем граф нулями
        CUDA_CHECK(cudaMemset(d_graph_, -1, graph_size));

        if (debug_mode) std::cout << "GPU Memory allocated and initialized." << std::endl;
    }
}

void KNNGraphWrapper::CleanupGPU() {
    if (d_data_) cudaFree(d_data_);
    if (d_graph_) cudaFree(d_graph_);
    if (d_labels_) cudaFree(d_labels_);
    d_data_ = nullptr;
    d_graph_ = nullptr;
    d_labels_ = nullptr;
}

bool KNNGraphWrapper::LoadDataset(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        std::cerr << "Error: Cannot open file " << filename << std::endl;
        return false;
    }

    file.read(reinterpret_cast<char*>(&n_points_), sizeof(int));
    file.read(reinterpret_cast<char*>(&n_dims_), sizeof(int));
    file.read(reinterpret_cast<char*>(&k_), sizeof(int));

    data_.resize(n_points_ * n_dims_);
    labels_.resize(n_points_);

    file.read(reinterpret_cast<char*>(data_.data()), n_points_ * n_dims_ * sizeof(float));
    file.read(reinterpret_cast<char*>(labels_.data()), n_points_ * sizeof(int));

    file.close();

    if (debug_mode) {
        std::cout << "Loaded dataset: " << n_points_ << " points, " << n_dims_ << " dims." << std::endl;
    }
    return true;
}

bool KNNGraphWrapper::SaveDataset(const std::string& filename) {
    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) return false;

    file.write(reinterpret_cast<const char*>(&n_points_), sizeof(int));
    file.write(reinterpret_cast<const char*>(&n_dims_), sizeof(int));
    file.write(reinterpret_cast<const char*>(&k_), sizeof(int));

    file.write(reinterpret_cast<const char*>(data_.data()), n_points_ * n_dims_ * sizeof(float));
    file.write(reinterpret_cast<const char*>(labels_.data()), n_points_ * sizeof(int));

    file.close();
    return true;
}

void KNNGraphWrapper::ResetSubset(int start_idx, int count) {
    if (start_idx < 0 || start_idx + count > n_points_) return;

    // Очистка памяти GPU для подмножества
    size_t data_size = count * n_dims_ * sizeof(float);
    size_t graph_size = count * max_degree_ * sizeof(int);
    size_t label_size = count * sizeof(int);

    // Вычисляем смещение в исходных массивах
    float* subset_data = data_.data() + start_idx * n_dims_;
    int* subset_labels = labels_.data() + start_idx;

    // Копируем данные в GPU
    if (d_data_) {
        CUDA_CHECK(cudaMemcpy(d_data_, subset_data, data_size, cudaMemcpyHostToDevice));
    }
    if (d_labels_) {
        CUDA_CHECK(cudaMemcpy(d_labels_, subset_labels, label_size, cudaMemcpyHostToDevice));
    }
    if (d_graph_) {
        CUDA_CHECK(cudaMemset(d_graph_, -1, graph_size));
    }

    if (debug_mode) {
        std::cout << "Reset GPU subset: indices " << start_idx << " to " << (start_idx + count - 1) << std::endl;
    }
}

bool KNNGraphWrapper::BuildGraph() {
    if (n_points_ == 0 || n_dims_ == 0) {
        std::cerr << "Error: No data to build graph." << std::endl;
        return false;
    }

    InitializeGPU();

    int threads = 256;
    int blocks = (n_points_ + threads - 1) / threads;

    if (debug_mode) {
        std::cout << "Building graph with " << blocks << " blocks..." << std::endl;
    }

    BuildKNNGraphKernel<<<blocks, threads>>>(
        d_data_, 
        d_graph_, 
        n_points_, 
        n_dims_, 
        k_, 
        max_degree_
    );

    CUDA_CHECK(cudaGetLastError());
    CUDA_CHECK(cudaDeviceSynchronize());

    return true;
}

bool KNNGraphWrapper::SaveGraph(const std::string& filename) {
    // Сохраняем только граф (индексы соседей)
    if (d_graph_ == nullptr) return false;

    std::ofstream file(filename, std::ios::binary);
    if (!file.is_open()) return false;

    file.write(reinterpret_cast<const char*>(&n_points_), sizeof(int));
    file.write(reinterpret_cast<const char*>(&max_degree_), sizeof(int));

    // Копируем граф с GPU на хост
    size_t graph_size = n_points_ * max_degree_ * sizeof(int);
    std::vector<int> host_graph(n_points_ * max_degree_);
    CUDA_CHECK(cudaMemcpy(host_graph.data(), d_graph_, graph_size, cudaMemcpyDeviceToHost));

    file.write(reinterpret_cast<const char*>(host_graph.data()), graph_size);
    file.close();
    return true;
}

bool KNNGraphWrapper::LoadGraph(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) return false;

    int temp_n_points, temp_max_degree;
    file.read(reinterpret_cast<char*>(&temp_n_points), sizeof(int));
    file.read(reinterpret_cast<char*>(&temp_max_degree), sizeof(int));

    if (temp_n_points != n_points_ || temp_max_degree != max_degree_) {
        std::cerr << "Error: Graph dimensions mismatch." << std::endl;
        return false;
    }

    size_t graph_size = n_points_ * max_degree_ * sizeof(int);
    std::vector<int> host_graph(n_points_ * max_degree_);

    file.read(reinterpret_cast<char*>(host_graph.data()), graph_size);
    file.close();

    // Копируем обратно на GPU
    InitializeGPU();
    CUDA_CHECK(cudaMemcpy(d_graph_, host_graph.data(), graph_size, cudaMemcpyHostToDevice));

    return true;
}

std::vector<int> KNNGraphWrapper::Query(const std::vector<float>& query_point, int k, bool verbose) {
    if (query_point.size() != n_dims_) {
        std::cerr << "Error: Query dimension mismatch." << std::endl;
        return {};
    }

    // 1. Выделение памяти под результат на GPU
    int* d_result_indices;
    float* d_result_dists;
    CUDA_CHECK(cudaMalloc(&d_result_indices, n_points_ * k * sizeof(int)));
    CUDA_CHECK(cudaMalloc(&d_result_dists, n_points_ * k * sizeof(float)));

    // 2. Выполнение поиска
    int threads = 256;
    int blocks = (n_points_ + threads - 1) / threads;

    SearchKNNGraphKernel<<<blocks, threads>>>(
        d_data_, 
        d_graph_, 
        query_point.data(), 
        d_result_indices, 
        d_result_dists, 
        n_points_, 
        n_dims_, 
        k, 
        max_degree_, 
        max_ef_
    );

    CUDA_CHECK(cudaGetLastError());
    CUDA_CHECK(cudaDeviceSynchronize());

    // 3. Копирование результата на хост
    std::vector<int> host_results(n_points_ * k);
    std::vector<float> host_dists(n_points_ * k);
    CUDA_CHECK(cudaMemcpy(host_results.data(), d_result_indices, n_points_ * k * sizeof(int), cudaMemcpyDeviceToHost));
    CUDA_CHECK(cudaMemcpy(host_dists.data(), d_result_dists, n_points_ * k * sizeof(float), cudaMemcpyDeviceToHost));

    // 4. Сборка результата (возвращаем топ-K для конкретного запроса, 
    //    но в этом примере ядро вернуло топ-K для всех точек, поэтому берем только первую точку)
    //    *Примечание: Реальный поиск возвращает топ-K для конкретного ID*

    // Упрощение: в данном примере SearchKNNGraphKernel вернул топ-K для всех точек.
    // Нам нужен результат только для первой точки (index 0), так как query_point один.
    // Но ядро не знает ID запроса. 
    // В полной реализации здесь нужен отдельный поток или логика.

    // Для простоты примера, мы вернем первые k соседей первой точки из массива результатов
    std::vector<int> final_results(k);
    for(int i=0; i<k; ++i) {
        final_results[i] = host_results[i]; // Это индекс соседа для точки 0
    }

    if (verbose && debug_mode) {
        std::cout << "Query neighbors: ";
        for (int i = 0; i < k; ++i) {
            std::cout << final_results[i] << " ";
        }
        std::cout << std::endl;
    }

    cudaFree(d_result_indices);
    cudaFree(d_result_dists);

    return final_results;
}

float KNNGraphWrapper::Evaluate(bool verbose) {
    if (labels_.empty()) return -1.0f;

    // 1. Сравниваем результаты графа с эталонными метками (Brute Force)
    //    Для простоты возьмем первую точку
    std::vector<float> query = {data_[0], data_[1]}; // Берем первые 2 компоненты

    std::vector<int> graph_results = Query(query, k_, verbose);
    std::vector<int> brute_results;

    // Находим топ-K с помощью CPU (Brute Force) для сравнения
    BruteForceSearch(query, k_, brute_results);

    // Сравниваем метки
    int correct = 0;
    for (int i = 0; i < k_; ++i) {
        if (graph_results[i] == brute_results[i]) {
            correct++;
        }
    }

    float precision = (float)correct / k_;

    if (verbose || debug_mode) {
        std::cout << "Evaluation (Precision@K): " << precision << " (" << correct << "/" << k_ << ")" << std::endl;
    }

    return precision;
}

float KNNGraphWrapper::BruteForceSearch(const std::vector<float>& query, int k, std::vector<int>& result) {
    // Простая реализация на CPU для генерации эталона
    std::vector<std::pair<float, int>> dists;
    dists.reserve(n_points_);

    for (int i = 0; i < n_points_; ++i) {
        float d = L2Distance(query.data(), &data_[i * n_dims_], n_dims_);
        dists.push_back({d, i});
    }

    std::sort(dists.begin(), dists.end());

    result.clear();
    for (int i = 0; i < k; ++i) {
        result.push_back(dists[i].second);
    }

    return dists[k].first; // Возвращаем дистанцию до k-го соседа
}
```

