    cpuGrid[gridSize * gridSize / 2 + gridSize / 2 - 1] = 1;
    cpuGrid[gridSize * gridSize / 2 + gridSize / 2 - gridSize] = 1;
    cpuGrid[gridSize * gridSize / 2 + gridSize / 2 + gridSize] = 1;

    // (Здесь должна быть логика последовательного обновления CPU, 
