	b.timeout = timeout
	return b
}

// build creates and returns a configured HTTP transport
func (b *TransportBuilder) build() (*http.Transport, error) {
	transport := &http.Transport{
		TLSClientConfig: b.tlsConfig,
	}

	// Configure proxy settings
	if b.noProxy {
		transport.Proxy = http.ProxyURL(nil)
	} else if b.proxyURL != nil {
		proxyFunc := http.ProxyURL(b.proxyURL)
