func (tb *TransportBuilder) build() (*http.Transport, error) {
	// Start with default transport
	transport := &http.Transport{
		Proxy: http.ProxyFromEnvironment,
		DialContext: (&net.Dialer{
			Timeout:   30 * time.Second,
			KeepAlive: 30 * time.Second,
			DualStack: true,
		}).DialContext,
		MaxIdleConns:          100,
		IdleConnTimeout:       90 * time.Second,
		TLSHandshakeTimeout:   10 * time.Second,
		ExpectContinueTimeout: 1 * time.Second,
	}

	// Apply proxy configuration
	if tb.proxyURL != nil {
		if tb.proxyUsername != "" && tb.proxyPassword != "" {
			transport.Proxy = func(req *http.Request) (*url.URL, error) {
				req.URL.Scheme = tb.proxyURL.Scheme
				req.URL.Host = tb.proxyURL.Host
				req.SetBasicAuth(tb.proxyUsername, tb.proxyPassword)
				return tb.proxyURL, nil
			}
		} else {
			transport.Proxy = http.ProxyURL(tb.proxyURL)
		}
	} else if tb.disableProxy {
		transport.Proxy = nil
	}

	// Apply TLS configuration
	if tb.tlsConfig != nil {
		transport.TLSClientConfig = tb.tlsConfig
	}

	// Apply global timeout
	if tb.timeout > 0 {
		transport.DialContext = (&net.Dialer{
			Timeout:   tb.timeout,
			KeepAlive: 30 * time.Second,
			DualStack: true,
		}).DialContext
		transport.TLSHandshakeTimeout = tb.timeout
	}

	return transport, nil
}
```

Структура `TransportBuilder` должна содержать следующие поля:

