		transport.RegisterProtocol("https", &http.Transport{
			TLSClientConfig: tb.tlsConfig,
			Proxy:           transport.Proxy,
			DialContext: (&net.Dialer{
				Timeout:   tb.timeout,
				KeepAlive: 30 * time.Second,
				DualStack: true,
			}).DialContext,
		})
	}

	// Apply custom headers
	if len(tb.headers) > 0 {
		originalRoundTripper := transport.RoundTripper
		if originalRoundTripper == nil {
			originalRoundTripper = http.DefaultTransport
		}
		transport.RoundTripper = &headerRoundTripper{
			base: originalRoundTripper,
			headers: tb.headers,
		}
	}

	// Apply timeout
	if tb.timeout > 0 {
		originalRoundTripper := transport.RoundTripper
		if originalRoundTripper == nil {
			originalRoundTripper = http.DefaultTransport
		}
		transport.RoundTripper = &timeoutRoundTripper{
			base:   originalRoundTripper,
			timeout: tb.timeout,
		}
	}

	return transport, nil
}

type headerRoundTripper struct {
	base    http.RoundTripper
	headers map[string]string
}

func (hrt *headerRoundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	for key, value := range hrt.headers {
		req.Header.Set(key, value)
	}
	return hrt.base.RoundTrip(req)
}

type timeoutRoundTripper struct {
	base    http.RoundTripper
	timeout time.Duration
}

func (trt *timeoutRoundTripper) RoundTrip(req *http.Request) (*http.Response, error) {
	ctx, cancel := context.WithTimeout(req.Context(), trt.timeout)
	defer cancel()
	req = req.WithContext(ctx)
	return trt.base.RoundTrip(req)
}
