### Ядро CUDA

```c
__global__ void game_of_life_kernel(unsigned char *curr, unsigned char *next,
                                    int rows, int cols)
{
    int i = blockIdx.y * blockDim.y + threadIdx.y;
    int j = blockIdx.x * blockDim.x + threadIdx.x;

    if (i >= rows || j >= cols) return;

    int live_neighbors = 0;
    for (int di = -1; di <= 1; di++) {
        for (int dj = -1; dj <= 1; dj++) {
            if (di == 0 && dj == 0) continue;
            int ni = i + di, nj = j + dj;
            if (ni >= 0 && ni < rows && nj >= 0 && nj < cols) {
                live_neighbors += curr[ni * cols + nj];
            }
        }
    }

    if (curr[i * cols + j] == 1) {
        next[i * cols + j] = (live_neighbors == 2 || live_neighbors == 3) ? 1 : 0;
    } else {
        next[i * cols + j] = (live_neighbors == 3) ? 1 : 0;
    }
}
```

### Вспомогательные функции для обмена границами

```c
void exchange_boundaries(unsigned char *grid, int rows, int cols,
                         int rank, int nGPUs, cudaStream_t stream)
{
    int top_peer = (rank - 1 + nGPUs) % nGPUs;
    int bottom_peer = (rank + 1) % nGPUs;

    int send_top_row = (rank == 0) ? rows - 1 : 0;
    int recv_top_row = (rank == 0) ? rows - 1 : -1;
    int send_bottom_row = (rank == nGPUs - 1) ? 0 : rows - 1;
    int recv_bottom_row = (rank == nGPUs - 1) ? -1 : rows;

    // Асинхронная передача верхней строки
    if (rank != 0) {
        cudaMemcpyAsync(grid + (-1) * cols, grid + 0 * cols, cols * sizeof(unsigned char),
                        cudaMemcpyDeviceToDevice, stream);
    }
    if (rank != nGPUs - 1) {
        cudaMemcpyAsync(grid + rows * cols, grid + (rows - 1) * cols, cols * sizeof(unsigned char),
                        cudaMemcpyDeviceToDevice, stream);
    }
}
```

### Основной цикл симуляции

```c
void simulate(unsigned char *d_grid, unsigned char *d_temp,
              int total_rows, int total_cols,
              int nGPUs, int nIters)
{
    int rows_per_gpu = (total_rows + nGPUs - 1) / nGPUs;
    int streams_per_gpu = 2;
    cudaStream_t *streams = new cudaStream_t[nGPUs * streams_per_gpu];

    for (int i = 0; i < nGPUs * streams_per_gpu; i++) {
        cudaStreamCreate(&streams[i]);
    }

    for (int iter = 0; iter < nIters; iter++) {
        for (int gpu = 0; gpu < nGPUs; gpu++) {
            int start_row = gpu * rows_per_gpu;
            int end_row = min((gpu + 1) * rows_per_gpu, total_rows);
            int local_rows = end_row - start_row;

            // Запуск ядра на каждом GPU
            dim3 block(16, 16);
            dim3 grid((total_cols + block.x - 1) / block.x,
                      (local_rows + block.y - 1) / block.y);

            int stream_idx = (iter % 2) * nGPUs + gpu;
            game_of_life_kernel<<<grid, block, 0, streams[stream_idx]>>>(
                d_grid + (start_row - 1) * total_cols,
                d_temp + (start_row - 1) * total_cols,
                local_rows + 2, total_cols);
        }

        // Обмен границами между GPU
        for (int gpu = 0; gpu < nGPUs; gpu++) {
            int stream_idx = (iter % 2) * nGPUs + gpu;
            exchange_boundaries(d_grid, rows_per_gpu + 2, total_cols,
                                gpu, nGPUs, streams[stream_idx]);
        }

        // Синхронизация и обмен указателями
        for (int gpu = 0; gpu < nGPUs; gpu++) {
            int stream_idx = (iter % 2) * nGPUs + gpu;
            cudaStreamSynchronize(streams[stream_idx]);
        }
        std::swap(d_grid, d_temp);
    }

    for (int i = 0; i < nGPUs * streams_per_gpu; i++) {
        cudaStreamDestroy(streams[i]);
    }
    delete[] streams;
}
```

### Регрессионный тест и сохранение изображений

```c
void save_grid_to_file(unsigned char *grid, int rows, int cols, const char *filename)
{
    std::ofstream file(filename, std::ios::binary);
    file << "P5\n" << cols << " " << rows << "\n255\n";
    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            unsigned char pixel = grid[i * cols + j] ? 255 : 0;
            file.write((char*)&pixel, 1);
        }
    }
}

bool compare_grids(unsigned char *grid1, unsigned char *grid2, int rows, int cols)
{
    for (int i = 0; i < rows * cols; i++) {
        if (grid1[i] != grid2[i]) return false;
    }
    return true;
}
```

