int main()
{
    const int total_rows = 1024, total_cols = 1024;
    const int nGPUs = 2, nIters = 100;

    unsigned char *h_grid = new unsigned char[total_rows * total_cols];
    // Инициализация начального состояния (например, glider)
    for (int i = 0; i < total_rows * total_cols; i++) h_grid[i] = 0;
    h_grid[1 * total_cols + 2] = 1;
    h_grid[2 * total_cols + 0] = 1;
    h_grid[2 * total_cols + 1] = 1;
    h_grid[2 * total_cols + 2] = 1;
    h_grid[3 * total_cols + 1] = 1;

    unsigned char *d_grid = nullptr, *d_temp = nullptr;
    cudaMalloc(&d_grid, (total_rows + 2) * total_cols * sizeof(unsigned char));
    cudaMalloc(&d_temp, (total_rows + 2) * total_cols * sizeof(unsigned char));
    cudaMemcpy(d_grid, h_grid, total_rows * total_cols * sizeof(unsigned char),
               cudaMemcpyHostToDevice);

    simulate(d_grid, d_temp, total_rows, total_cols, nGPUs, nIters);

    cudaMemcpy(h_grid, d_grid, total_rows * total_cols * sizeof(unsigned char),
               cudaMemcpyDeviceToHost);

    save_grid_to_file(h_grid, total_rows, total_cols, "output.pgm");

    // Регрессионный тест: сравнение с эталонным результатом
    unsigned char *ref_grid = new unsigned char[total_rows * total_cols];
    // ... (эталонный результат)
    if (!compare_grids(h_grid, ref_grid, total_rows, total_cols)) {
        std::cerr << "Test failed!\n";
    }

    delete[] h_grid;
    delete[] ref_grid;
    cudaFree(d_grid);
    cudaFree(d_temp);
    return 0;
}
